import React, { useState, useEffect } from 'react';
import Header from './components/Header';
import ProductCard from './components/ProductCard';
import CartModal from './components/CartModal';
import CheckoutModal from './components/CheckoutModal';
import ProductModal from './components/ProductModal';
import Footer from './components/Footer';
import { apiService } from './services/api';

const MainApp = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    email: '',
    phone: '',
    address: ''
  });
  const [isMenuOpen, setIsMenuOpen] = useState(false);

  // Fetch products on component mount
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const productsData = await apiService.getProducts();
        setProducts(productsData);
      } catch (error) {
        console.error('Error fetching products:', error);
        // Fallback to dummy data
        setProducts([
          {
            id: 1,
            name: "شنطة جلد طبيعي",
            description: "شنطة أنيقة مصنوعة من الجلد الطبيعي عالي الجودة",
            price: 299.99,
            image_url: "https://images.unsplash.com/photo-1548036328-c9fa89d128fa?w=400",
            category: "شنط يد",
            stock_quantity: 10
          },
          {
            id: 2,
            name: "شنطة كروس باج",
            description: "شنطة عملية ومريحة للاستخدام اليومي",
            price: 199.99,
            image_url: "https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400",
            category: "شنط كروس",
            stock_quantity: 15
          },
          {
            id: 3,
            name: "شنطة كلاتش",
            description: "شنطة أنيقة للمناسبات الخاصة",
            price: 399.99,
            image_url: "https://images.unsplash.com/photo-1594223274512-ad4803739b7c?w=400",
            category: "شنط كلاتش",
            stock_quantity: 8
          }
        ]);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.id === product.id);
      if (existingItem) {
        return prevCart.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
  };

  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
      return;
    }
    
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === productId
          ? { ...item, quantity: newQuantity }
          : item
      )
    );
  };

  const removeFromCart = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const handleCheckout = async () => {
    try {
      const orderData = {
        customer_info: customerInfo,
        items: cart.map(item => ({
          product_id: item.id,
          quantity: item.quantity,
          price: item.price
        })),
        total_amount: getTotalPrice()
      };

      await apiService.createOrder(orderData);
      
      // Clear cart and customer info
      setCart([]);
      setCustomerInfo({ name: '', email: '', phone: '', address: '' });
      setShowCheckout(false);
      setShowCart(false);
      
      alert('تم إرسال طلبك بنجاح! سنتواصل معك قريباً.');
    } catch (error) {
      console.error('Error creating order:', error);
      alert('حدث خطأ أثناء إرسال الطلب. يرجى المحاولة مرة أخرى.');
    }
  };

  const cartCount = cart.reduce((total, item) => total + item.quantity, 0);

  return (
    <div className="min-h-screen bg-black">
      <Header
        cartCount={cartCount}
        toggleCart={() => setShowCart(true)}
        toggleMenu={() => setIsMenuOpen(!isMenuOpen)}
      />

      {/* Hero Section */}
      <section id="home" className="relative min-h-screen bg-gradient-to-r from-black via-gray-900 to-black text-white py-20 overflow-hidden">
        {/* المحتوى */}
        <div className="relative z-10 container mx-auto px-4 flex items-center min-h-screen">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 lg:gap-12 items-center w-full">
            
            {/* النص */}
            <motion.div
              className="text-center lg:text-right order-2 lg:order-1 mt-8 lg:mt-0"
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8 }}
            >
              <h1 className="text-4xl md:text-6xl font-bold mb-6 leading-tight">
                Hand Made
                <span className="block text-gray-100 font-light">Bags</span>
              </h1>
              <p className="text-lg md:text-2xl mb-8 text-gray-300">
                High-quality handmade bags crafted with care and precision
              </p>
              <div className="flex gap-4 justify-center lg:justify-start">
                <motion.a
                  href="#products"
                  className="bg-white text-black px-8 py-3 rounded-lg font-semibold shadow-lg"
                  whileHover={{ scale: 1.05 }}
                  whileTap={{ scale: 0.95 }}
                >
                  Shop Now
                </motion.a>
                <motion.button
                  className="border-2 border-white px-8 py-3 rounded-lg font-semibold"
                  whileHover={{ scale: 1.05, backgroundColor: "#fff", color: "#000" }}
                  whileTap={{ scale: 0.95 }}
                >
                  Learn More
                </motion.button>
              </div>
            </motion.div>

            {/* صورة المنتج */}
            <motion.div
              className="flex justify-center lg:justify-start order-1 lg:order-2"
              initial={{ opacity: 0, scale: 0.8 }}
              animate={{ opacity: 1, scale: 1 }}
              transition={{ duration: 1 }}
            >
              <img
                src="https://images.unsplash.com/photo-1584917865442-de89df76afd3?w=800&q=80"
                alt="Handmade leather bag"
                className="w-80 h-80 lg:w-96 lg:h-96 object-cover rounded-2xl shadow-2xl transform rotate-3 hover:rotate-0 transition-transform duration-500"
              />
            </motion.div>
          </div>
        </div>
      </section>

      {/* Products Section */}
      <section id="products" className="py-16 bg-gray-900 border-t border-gray-700">
        <div className="container mx-auto px-4">
          <h2 className="text-3xl font-bold text-center mb-12 text-white">Products</h2>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-8">
            {products.map((product, index) => (
              <motion.div
                key={product.id}
                initial={{ opacity: 0, y: 50 }}
                whileInView={{ opacity: 1, y: 0 }}
                viewport={{ once: true }}
                transition={{ duration: 0.6, delay: index * 0.1 }}
                whileHover={{ scale: 1.05 }}
              >
                <ProductCard
                  product={product}
                  onAddToCart={addToCart}
                  onViewDetails={() => setSelectedProduct(product)}
                />
              </motion.div>
            ))}
          </div>
        </div>
      </section>

      {/* باقي الأقسام نفس الكود بتاعك، ممكن نضيف نفس فكرة motion.div للـ about و contact */}
      <Footer />
    </div>
  );
};

export default MainApp;
