import React from 'react';
import { ShoppingCart, Eye } from 'lucide-react';
import { motion } from 'framer-motion';

const ProductCard = ({ product, onAddToCart, onViewDetails }) => {
  return (
    <motion.div
      whileHover={{ scale: 1.03 }}
      whileTap={{ scale: 0.97 }}
      className="bg-white rounded-2xl shadow-md overflow-hidden hover:shadow-2xl transition-shadow duration-300 border border-gray-200"
    >
      {/* صورة المنتج */}
      <div className="relative">
        <motion.img
          src={product.image_url}
          alt={product.name}
          className="w-full h-40 sm:h-48 md:h-56 lg:h-64 object-cover transition duration-500"
        />

        {/* الكاتيجوري */}
        <motion.span
          initial={{ opacity: 0, y: -10 }}
          animate={{ opacity: 1, y: 0 }}
          className="absolute top-4 right-4 bg-black text-white px-3 py-1 rounded-full text-xs font-semibold shadow-lg"
        >
          {product.category}
        </motion.span>
      </div>

      {/* بيانات المنتج */}
      <div className="p-4 sm:p-5">
        <h3 className="text-base sm:text-lg font-semibold text-gray-900 mb-2">{product.name}</h3>
        <p className="text-gray-500 text-xs sm:text-sm mb-3 line-clamp-2">{product.description}</p>

        <div className="flex justify-between items-center mb-3 sm:mb-4">
          <span className="text-lg sm:text-xl font-bold text-black">{product.price} جنيه</span>
          <span className="text-[10px] sm:text-xs text-gray-400">المخزون: {product.stock_quantity}</span>
        </div>

        {/* أزرار */}
        <div className="flex space-x-2 space-x-reverse">
          <motion.button
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            onClick={() => onAddToCart(product)}
            className="flex-1 bg-black text-white py-2 px-3 sm:px-4 rounded-lg hover:bg-gray-800 transition-colors flex items-center justify-center space-x-2 space-x-reverse text-xs sm:text-sm"
          >
            <ShoppingCart size={16} />
            <span>buy now</span>
          </motion.button>

          <motion.button
            whileHover={{ rotate: 10, scale: 1.1 }}
            whileTap={{ scale: 0.9 }}
            onClick={() => onViewDetails(product)}
            className="bg-gray-100 text-black py-2 px-3 sm:px-4 rounded-lg hover:bg-gray-200 transition-colors shadow-sm text-xs sm:text-sm"
          >
            <Eye size={16} />
          </motion.button>
        </div>
      </div>
    </motion.div>
  );
};

export default ProductCard;
