import React, { useState, useEffect } from 'react';
import { 
  LogIn, 
  LogOut, 
  Plus, 
  Trash2, 
  Edit, 
  Eye, 
  Upload,
  Package,
  ShoppingCart,
  Users,
  X
} from 'lucide-react';

// Mock API service for demonstration
const apiService = {
  getToken: () => localStorage.getItem('authToken'),
  setToken: (token) => localStorage.setItem('authToken', token),
  removeToken: () => localStorage.removeItem('authToken'),
  
  login: async (credentials) => {
    // Simulate login
    console.log('Login attempted with:', credentials);
    apiService.setToken('mock-token');
    return { access_token: 'mock-token', token_type: 'bearer' };
  },
  
  logout: () => {
    apiService.removeToken();
  },
  
  getProducts: async () => {
    // Mock products data
    return [
      {
        id: 1,
        name: "Premium Leather Handbag",
        description: "Elegant handcrafted leather bag",
        price: 299.99,
        category: "Handbags",
        stock_quantity: 10,
        image_url: "https://images.unsplash.com/photo-1548036328-c9fa89d128fa?w=400",
        images: [
          "https://images.unsplash.com/photo-1548036328-c9fa89d128fa?w=400",
          "https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400"
        ]
      }
    ];
  },
  
  getOrders: async () => {
    return [
      {
        id: 1,
        customer_info: { name: "John Doe", email: "john@example.com" },
        items: [{ id: 1, quantity: 2 }],
        total_amount: 599.98,
        status: "pending",
        created_at: new Date().toISOString()
      }
    ];
  },
  
  createProduct: async (productData) => {
    console.log('Creating product:', productData);
    return { id: Date.now(), ...productData };
  },
  
  deleteProduct: async (id) => {
    console.log('Deleting product:', id);
    return { success: true };
  },
  
  updateOrderStatus: async (orderId, status) => {
    console.log('Updating order status:', orderId, status);
    return { success: true };
  },
  
  uploadFile: async (file) => {
    // Mock file upload
    const mockUrl = `https://via.placeholder.com/400?text=${file.name}`;
    return { url: mockUrl, filename: file.name };
  }
};

const AdminPanel = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [products, setProducts] = useState([]);
  const [orders, setOrders] = useState([]);
  const [loginForm, setLoginForm] = useState({ email: '', password: '' });
  const [productForm, setProductForm] = useState({
    name: '',
    description: '',
    price: '',
    category: '',
    stock_quantity: '',
    image_url: '', // الصورة الرئيسية
    images: [] // مجموعة الصور الإضافية
  });

  const [selectedFiles, setSelectedFiles] = useState([]);
  const [uploadedImageUrls, setUploadedImageUrls] = useState([]);
  const [isUploading, setIsUploading] = useState(false);
  const [activeTab, setActiveTab] = useState('products');

  useEffect(() => {
    const token = apiService.getToken();
    if (token) {
      setIsLoggedIn(true);
      fetchData();
    }
  }, []);

  const fetchData = async () => {
    try {
      const [productsData, ordersData] = await Promise.all([
        apiService.getProducts(),
        apiService.getOrders()
      ]);
      setProducts(productsData);
      setOrders(ordersData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      await apiService.login(loginForm);
      setIsLoggedIn(true);
      fetchData();
    } catch (error) {
      alert('فشل تسجيل الدخول: ' + error.message);
    }
  };

  const handleLogout = () => {
    apiService.logout();
    setIsLoggedIn(false);
    setProducts([]);
    setOrders([]);
  };

  // دالة رفع صور متعددة واحدة واحدة
  const handleMultipleFileUpload = async () => {
    if (selectedFiles.length === 0) return;
    
    setIsUploading(true);
    const newImageUrls = [];
    
    try {
      // رفع كل ملف لوحده
      for (let i = 0; i < selectedFiles.length; i++) {
        const file = selectedFiles[i];
        console.log(`Uploading file ${i + 1}/${selectedFiles.length}: ${file.name}`);
        
        const result = await apiService.uploadFile(file);
        newImageUrls.push(result.url);
        
        // إظهار التقدم
        console.log(`Uploaded: ${result.url}`);
      }
      
      // إضافة الصور المرفوعة للـ form
      setUploadedImageUrls(prev => [...prev, ...newImageUrls]);
      setProductForm(prev => ({
        ...prev,
        // إضافة الصور للـ images array
        images: [...prev.images, ...newImageUrls],
        // إذا مفيش صورة رئيسية، خلي أول صورة هي الرئيسية
        image_url: prev.image_url || newImageUrls[0] || ''
      }));
      
      // مسح الملفات المختارة
      setSelectedFiles([]);
      document.getElementById('multiple-file-input').value = '';
      
      alert(`تم رفع ${newImageUrls.length} صورة بنجاح!`);
      
    } catch (error) {
      console.error('Upload error:', error);
      alert('فشل في رفع بعض الصور: ' + error.message);
    } finally {
      setIsUploading(false);
    }
  };

  // دالة إزالة صورة من القائمة
  const removeUploadedImage = (indexToRemove) => {
    const imageToRemove = productForm.images[indexToRemove];
    
    setProductForm(prev => {
      const newImages = prev.images.filter((_, index) => index !== indexToRemove);
      return {
        ...prev,
        images: newImages,
        // إذا كانت الصورة المحذوفة هي الرئيسية، اختار أول صورة متبقية
        image_url: prev.image_url === imageToRemove ? (newImages[0] || '') : prev.image_url
      };
    });
    
    setUploadedImageUrls(prev => prev.filter((_, index) => index !== indexToRemove));
  };

  // دالة تعيين صورة كرئيسية
  const setAsMainImage = (imageUrl) => {
    setProductForm(prev => ({
      ...prev,
      image_url: imageUrl
    }));
  };

  const handleCreateProduct = async (e) => {
    e.preventDefault();
    try {
      const productData = {
        ...productForm,
        price: parseFloat(productForm.price),
        stock_quantity: parseInt(productForm.stock_quantity)
      };
      
      console.log("Submitting product:", productData);
      
      await apiService.createProduct(productData);
      
      // إعادة تعيين النموذج
      setProductForm({
        name: '',
        description: '',
        price: '',
        category: '',
        stock_quantity: '',
        image_url: '',
        images: []
      });
      setUploadedImageUrls([]);
      setSelectedFiles([]);
      
      fetchData();
      alert('تم إنشاء المنتج بنجاح!');
    } catch (error) {
      alert('فشل إنشاء المنتج: ' + error.message);
    }
  };

  const handleDeleteProduct = async (id) => {
    if (!window.confirm('هل أنت متأكد من حذف هذا المنتج؟')) return;
    
    try {
      await apiService.deleteProduct(id);
      fetchData();
      alert('تم حذف المنتج بنجاح!');
    } catch (error) {
      alert('فشل حذف المنتج: ' + error.message);
    }
  };

  const handleUpdateOrderStatus = async (orderId, status) => {
    try {
      await apiService.updateOrderStatus(orderId, status);
      fetchData();
      alert('تم تحديث حالة الطلب بنجاح!');
    } catch (error) {
      alert('فشل تحديث حالة الطلب: ' + error.message);
    }
  };

  if (!isLoggedIn) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="bg-white p-8 rounded-lg shadow-lg max-w-md w-full">
          <div className="text-center mb-8">
            <h1 className="text-2xl font-bold text-gray-800">تسجيل دخول المدير</h1>
            <p className="text-gray-600">أدخل بيانات الدخول للوصول للوحة التحكم</p>
          </div>
          
          <form onSubmit={handleLogin} className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                البريد الإلكتروني
              </label>
              <input
                type="email"
                required
                value={loginForm.email}
                onChange={(e) => setLoginForm({...loginForm, email: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
            
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-1">
                كلمة المرور
              </label>
              <input
                type="password"
                required
                value={loginForm.password}
                onChange={(e) => setLoginForm({...loginForm, password: e.target.value})}
                className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
              />
            </div>
            
            <button
              type="submit"
              className="w-full bg-purple-600 text-white py-2 rounded-md hover:bg-purple-700 transition-colors flex items-center justify-center space-x-2 space-x-reverse"
            >
              <LogIn size={16} />
              <span>تسجيل الدخول</span>
            </button>
          </form>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-lg">
        <div className="container mx-auto px-4 py-4">
          <div className="flex justify-between items-center">
            <h1 className="text-2xl font-bold text-gray-800">Control Manager</h1>
            <button
              onClick={handleLogout}
              className="bg-red-600 text-white px-4 py-2 rounded-md hover:bg-red-700 transition-colors flex items-center space-x-2 space-x-reverse"
            >
              <LogOut size={16} />
              <span>Logout</span>
            </button>
          </div>
        </div>
      </header>

      <div className="container mx-auto px-4 py-8">
        {/* Tabs */}
        <div className="flex space-x-4 space-x-reverse mb-8">
          <button
            onClick={() => setActiveTab('products')}
            className={`px-4 py-2 rounded-md flex items-center space-x-2 space-x-reverse ${
              activeTab === 'products' 
                ? 'bg-purple-600 text-white' 
                : 'bg-white text-gray-700 hover:bg-gray-100'
            }`}
          >
            <Package size={16} />
            <span>Products</span>
          </button>
          
          <button
            onClick={() => setActiveTab('orders')}
            className={`px-4 py-2 rounded-md flex items-center space-x-2 space-x-reverse ${
              activeTab === 'orders' 
                ? 'bg-purple-600 text-white' 
                : 'bg-white text-gray-700 hover:bg-gray-100'
            }`}
          >
            <ShoppingCart size={16} />
            <span>Orders</span>
          </button>
        </div>

        {/* Products Tab */}
        {activeTab === 'products' && (
          <div className="space-y-8">
            {/* Add Product Form */}
            <div className="bg-white p-6 rounded-lg shadow-lg">
              <h2 className="text-xl font-semibold mb-4">Add Product</h2>
              <form onSubmit={handleCreateProduct} className="space-y-6">
                {/* Basic Info */}
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Product Name</label>
                    <input
                      type="text"
                      required
                      value={productForm.name}
                      onChange={(e) => setProductForm({...productForm, name: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                    />
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Category</label>
                    <input
                      type="text"
                      required
                      value={productForm.category}
                      onChange={(e) => setProductForm({...productForm, category: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                    />
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Price</label>
                    <input
                      type="number"
                      step="0.01"
                      required
                      value={productForm.price}
                      onChange={(e) => setProductForm({...productForm, price: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                    />
                  </div>
                  
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">Available Quantity</label>
                    <input
                      type="number"
                      required
                      value={productForm.stock_quantity}
                      onChange={(e) => setProductForm({...productForm, stock_quantity: e.target.value})}
                      className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                    />
                  </div>
                </div>
                
                {/* Description */}
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                  <textarea
                    required
                    rows="3"
                    value={productForm.description}
                    onChange={(e) => setProductForm({...productForm, description: e.target.value})}
                    className="w-full px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                  />
                </div>
                
                {/* Multiple Images Upload Section */}
                <div className="space-y-4">
                  <h3 className="text-lg font-medium text-gray-800 flex items-center space-x-2">
                    <span>📸</span>
                    <span>Product Images</span>
                  </h3>
                  
                  {/* File Upload */}
                  <div className="space-y-3">
                    <div className="flex space-x-2 space-x-reverse">
                      <input
                        id="multiple-file-input"
                        type="file"
                        multiple
                        accept="image/*"
                        onChange={(e) => setSelectedFiles(Array.from(e.target.files))}
                        className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                      />
                      <button
                        type="button"
                        onClick={handleMultipleFileUpload}
                        disabled={selectedFiles.length === 0 || isUploading}
                        className={`px-4 py-2 rounded-md transition-colors flex items-center space-x-2 space-x-reverse ${
                          selectedFiles.length === 0 || isUploading
                            ? 'bg-gray-400 text-white cursor-not-allowed'
                            : 'bg-blue-600 text-white hover:bg-blue-700'
                        }`}
                      >
                        <Upload size={16} />
                        <span>{isUploading ? 'جاري الرفع...' : 'رفع الصور'}</span>
                      </button>
                    </div>
                    
                    {selectedFiles.length > 0 && (
                      <p className="text-sm text-gray-600">
                        تم اختيار {selectedFiles.length} ملف
                      </p>
                    )}
                    
                    {isUploading && (
                      <div className="text-sm text-blue-600">
                        جاري رفع الصور، انتظر من فضلك...
                      </div>
                    )}
                  </div>
                  
                  {/* Manual Image URL Input */}
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      رابط صورة إضافي (اختياري)
                    </label>
                    <div className="flex space-x-2 space-x-reverse">
                      <input
                        type="url"
                        placeholder="https://example.com/image.jpg"
                        className="flex-1 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-purple-500"
                        onKeyPress={(e) => {
                          if (e.key === 'Enter') {
                            e.preventDefault();
                            const url = e.target.value.trim();
                            if (url) {
                              setProductForm(prev => ({
                                ...prev,
                                images: [...prev.images, url],
                                image_url: prev.image_url || url
                              }));
                              e.target.value = '';
                            }
                          }
                        }}
                      />
                      <button
                        type="button"
                        onClick={() => {
                          const input = document.querySelector('input[type="url"]');
                          const url = input.value.trim();
                          if (url) {
                            setProductForm(prev => ({
                              ...prev,
                              images: [...prev.images, url],
                              image_url: prev.image_url || url
                            }));
                            input.value = '';
                          }
                        }}
                        className="bg-green-600 text-white px-4 py-2 rounded-md hover:bg-green-700 transition-colors"
                      >
                        إضافة
                      </button>
                    </div>
                  </div>
                  
                  {/* Display Current Images */}
                  {productForm.images.length > 0 && (
                    <div className="space-y-3">
                      <h4 className="text-md font-medium text-gray-700">
                        الصور المضافة ({productForm.images.length})
                      </h4>
                      <div className="grid grid-cols-2 md:grid-cols-4 lg:grid-cols-6 gap-3">
                        {productForm.images.map((imageUrl, index) => (
                          <div key={index} className="relative group">
                            <img
                              src={imageUrl}
                              alt={`صورة المنتج ${index + 1}`}
                              onError={(e) => {
                                e.currentTarget.src = 'https://via.placeholder.com/150?text=خطأ';
                              }}
                              className={`w-full h-20 object-cover rounded-lg border-2 cursor-pointer transition-all duration-200 ${
                                productForm.image_url === imageUrl 
                                  ? 'border-purple-500 ring-2 ring-purple-200 shadow-lg' 
                                  : 'border-gray-200 hover:border-purple-300 hover:shadow-md'
                              }`}
                              onClick={() => setAsMainImage(imageUrl)}
                            />
                            
                            {/* Main Image Badge */}
                            {productForm.image_url === imageUrl && (
                              <div className="absolute top-1 left-1 bg-purple-600 text-white text-xs px-1 py-0.5 rounded text-center">
                                رئيسية
                              </div>
                            )}
                            
                            {/* Remove Button */}
                            <button
                              type="button"
                              onClick={() => removeUploadedImage(index)}
                              className="absolute top-1 right-1 bg-red-600 text-white p-1 rounded-full opacity-0 group-hover:opacity-100 transition-opacity duration-200 hover:bg-red-700"
                            >
                              <X size={10} />
                            </button>
                            
                            {/* Image Index */}
                            <div className="absolute bottom-1 right-1 bg-black/70 text-white text-xs px-1 rounded">
                              {index + 1}
                            </div>
                          </div>
                        ))}
                      </div>
                      <p className="text-xs text-gray-500">
                        اضغط على صورة لجعلها الصورة الرئيسية. الصورة الرئيسية ستظهر أولاً في المعرض.
                      </p>
                    </div>
                  )}
                </div>
                
                {/* Submit Button */}
                <div>
                  <button
                    type="submit"
                    className="w-full bg-purple-600 text-white py-3 rounded-md hover:bg-purple-700 transition-colors flex items-center justify-center space-x-2 space-x-reverse"
                  >
                    <Plus size={16} />
                    <span>Add Product</span>
                  </button>
                </div>
              </form>
            </div>

            {/* Products List */}
            <div className="bg-white rounded-lg shadow-lg overflow-hidden">
              <div className="px-6 py-4 border-b">
                <h2 className="text-xl font-semibold">Products List</h2>
              </div>
              <div className="overflow-x-auto">
                <table className="w-full">
                  <thead className="bg-gray-50">
                    <tr>
                      <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Image</th>
                      <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
                      <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Category</th>
                      <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Price</th>
                      <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Stock</th>
                      <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                    </tr>
                  </thead>
                  <tbody className="bg-white divide-y divide-gray-200">
                    {products.map((product) => (
                      <tr key={product.id}>
                        <td className="px-6 py-4 whitespace-nowrap">
                          <div className="flex space-x-2">
                            {/* Main Image */}
                            {(() => {
                              const fallback = 'https://via.placeholder.com/48?text=No+Image';
                              const firstImage = Array.isArray(product.images) && product.images.length > 0 ? product.images[0] : undefined;
                              const imageSrc = product.image_url || firstImage || fallback;
                              return (
                                <img
                                  src={imageSrc}
                                  onError={(e) => { e.currentTarget.src = fallback; }}
                                  alt={product.name}
                                  className="h-12 w-12 object-cover rounded border-2 border-purple-200"
                                />
                              );
                            })()}
                            
                            {/* Additional Images Indicator */}
                            {Array.isArray(product.images) && product.images.length > 1 && (
                              <div className="flex flex-col justify-center">
                                <span className="text-xs text-gray-500 bg-gray-100 px-2 py-1 rounded">
                                  +{product.images.length - 1} more
                                </span>
                              </div>
                            )}
                          </div>
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                          {product.name}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {product.category}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          ${product.price}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                          {product.stock_quantity}
                        </td>
                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                          <button
                            onClick={() => handleDeleteProduct(product.id)}
                            className="text-red-600 hover:text-red-900 ml-2"
                          >
                            <Trash2 size={16} />
                          </button>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}

        {/* Orders Tab */}
        {activeTab === 'orders' && (
          <div className="bg-white rounded-lg shadow-lg overflow-hidden">
            <div className="px-6 py-4 border-b">
              <h2 className="text-xl font-semibold">Orders List</h2>
            </div>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Order Number</th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Client</th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Products</th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Total</th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Date</th>
                    <th className="px-6 py-3 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {orders.map((order) => (
                    <tr key={order.id}>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                        #{order.id}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        <div>
                          <div className="font-medium">{order.customer_info.name}</div>
                          <div className="text-gray-400">{order.customer_info.email}</div>
                        </div>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {order.items?.length || 0} Product(s)
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        ${order.total_amount}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap">
                        <select
                          value={order.status}
                          onChange={(e) => handleUpdateOrderStatus(order.id, e.target.value)}
                          className="text-sm border border-gray-300 rounded-md px-2 py-1"
                        >
                          <option value="pending">Pending</option>
                          <option value="confirmed">Confirmed</option>
                          <option value="processing">Processing</option>
                          <option value="shipped">Shipped</option>
                          <option value="delivered">Delivered</option>
                          <option value="cancelled">Cancelled</option>
                        </select>
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                        {new Date(order.created_at).toLocaleDateString('en-US')}
                      </td>
                      <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                        <button className="text-blue-600 hover:text-blue-900">
                          <Eye size={16} />
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default AdminPanel;
