import React, { useEffect, useState } from 'react';
import { X, Plus, Minus, Trash2, Phone, MessageCircle, Instagram, ShoppingBag } from 'lucide-react';

const CartModal = ({ cart, updateQuantity, removeFromCart, getTotalPrice, onClose, onCheckout }) => {
  const [showInstructions, setShowInstructions] = useState(false);
  const [animateItems, setAnimateItems] = useState(false);

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    setAnimateItems(true);
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, []);

  const handleCheckout = () => {
    setShowInstructions(true);
  };

  const handleGotIt = () => {
    onCheckout();
  };

  const depositAmount = (getTotalPrice() * 0.5).toFixed(2);

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4 animate-fade-in">
      <div className="bg-white w-full max-w-2xl max-h-[90vh] overflow-hidden shadow-2xl animate-slide-up">
        {/* Header */}
        <div className="relative p-6 bg-gradient-to-r from-purple-600 to-blue-600 text-white">
          <div className="flex justify-between items-center">
            <div className="flex items-center space-x-3">
              <div className="p-2 bg-white/20 rounded-lg backdrop-blur-sm">
                {showInstructions ? (
                  <div className="text-2xl">âœ…</div>
                ) : (
                  <ShoppingBag size={24} />
                )}
              </div>
              <h2 className="text-2xl font-bold tracking-wide">
                {showInstructions ? 'Order Confirmation' : 'Shopping Cart'}
              </h2>
            </div>
            <button
              onClick={onClose}
              className="p-2 hover:bg-white/20 rounded-lg transition-all duration-200 transform hover:scale-105"
            >
              <X size={24} />
            </button>
          </div>
          <div className="absolute inset-0 bg-gradient-to-r from-purple-600/50 to-blue-600/50 animate-pulse"></div>
        </div>
        
        {!showInstructions ? (
          <>
            {/* Cart Items */}
            <div className="p-6 overflow-y-auto max-h-96">
              {cart.length === 0 ? (
                <div className="text-center py-16">
                  <div className="w-24 h-24 mx-auto mb-6 bg-gray-100 rounded-full flex items-center justify-center">
                    <ShoppingBag size={40} className="text-gray-400" />
                  </div>
                  <h3 className="text-xl font-semibold text-gray-700 mb-2">Your cart is empty</h3>
                  <p className="text-gray-500">Add some amazing handmade items!</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {cart.map((item, index) => {
                    const fallbackImage = 'https://via.placeholder.com/100x100?text=No+Image';
                    const firstImage = Array.isArray(item.images) && item.images.length > 0 ? item.images[0] : undefined;
                    const imageSrc = item.image_url || firstImage || fallbackImage;
                    
                    return (
                      <div 
                        key={item.id} 
                        className={`group flex items-center space-x-4 p-5 bg-gradient-to-r from-gray-50 to-gray-100 rounded-xl border border-gray-200 hover:from-blue-50 hover:to-purple-50 hover:border-purple-200 transition-all duration-300 transform hover:scale-[1.02] hover:shadow-lg ${animateItems ? 'animate-in slide-in-from-left duration-500' : ''}`}
                        style={{ animationDelay: `${index * 100}ms` }}
                      >
                        <div className="relative">
                          <img 
                            src={imageSrc} 
                            alt={item.name}
                            onError={(e) => {
                              e.target.src = fallbackImage;
                            }}
                            className="w-20 h-20 object-cover rounded-xl shadow-md group-hover:shadow-lg transition-shadow duration-300"
                          />
                          <div className="absolute inset-0 bg-gradient-to-t from-black/20 to-transparent rounded-xl"></div>
                        </div>
                        
                        <div className="flex-1 min-w-0">
                          <h3 className="font-semibold text-gray-900 text-lg mb-1 truncate group-hover:text-purple-700 transition-colors">
                            {item.name}
                          </h3>
                          <p className="text-gray-600 font-medium">EGP {item.price}</p>
                          <div className="text-sm text-gray-500 mt-1">
                            Subtotal: EGP {(item.price * item.quantity).toFixed(2)}
                          </div>
                        </div>
                        
                        <div className="flex items-center space-x-3 bg-white rounded-lg p-2 shadow-sm">
                          <button
                            onClick={() => updateQuantity(item.id, item.quantity - 1)}
                            className="w-8 h-8 bg-red-100 hover:bg-red-200 text-red-600 transition-all duration-200 flex items-center justify-center rounded-lg transform hover:scale-105"
                          >
                            <Minus size={16} />
                          </button>
                          <span className="w-12 text-center font-bold text-lg text-gray-800">{item.quantity}</span>
                          <button
                            onClick={() => updateQuantity(item.id, item.quantity + 1)}
                            className="w-8 h-8 bg-green-100 hover:bg-green-200 text-green-600 transition-all duration-200 flex items-center justify-center rounded-lg transform hover:scale-105"
                          >
                            <Plus size={16} />
                          </button>
                        </div>
                        
                        <button
                          onClick={() => removeFromCart(item.id)}
                          className="p-2 text-red-500 hover:text-red-700 hover:bg-red-50 rounded-lg transition-all duration-200 transform hover:scale-105"
                        >
                          <Trash2 size={20} />
                        </button>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
            
            {/* Cart Footer */}
            {cart.length > 0 && (
              <div className="p-6 border-t border-gray-200 bg-gradient-to-r from-gray-50 to-gray-100">
                <div className="bg-white rounded-xl p-4 shadow-sm mb-4">
                  <div className="flex justify-between items-center mb-2">
                    <span className="text-gray-600">Items ({cart.reduce((sum, item) => sum + item.quantity, 0)})</span>
                    <span className="font-medium">EGP {getTotalPrice().toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between items-center text-xl font-bold text-gray-900">
                    <span>Total:</span>
                    <span className="text-green-600">EGP {getTotalPrice().toFixed(2)}</span>
                  </div>
                </div>
                <button
                  onClick={handleCheckout}
                  className="w-full bg-gradient-to-r from-purple-600 to-blue-600 text-white py-4 font-semibold text-lg tracking-wide uppercase transition-all duration-300 hover:from-purple-700 hover:to-blue-700 transform hover:scale-[1.02] rounded-xl shadow-lg hover:shadow-xl"
                >
                  <span className="flex items-center justify-center space-x-2">
                    <ShoppingBag size={20} />
                    <span>Proceed to Checkout</span>
                  </span>
                </button>
              </div>
            )}
          </>
        ) : (
          /* Instructions Section */
          <div className="p-6 overflow-y-auto max-h-96">
            <div className="space-y-6">
              {/* Order Summary Card */}
              <div className="bg-gradient-to-br from-green-50 to-emerald-50 p-6 rounded-xl border border-green-200">
                <h3 className="text-xl font-bold text-gray-900 mb-4 flex items-center">
                  <span className="text-2xl mr-3 animate-bounce">ðŸŽ‰</span>
                  Order Summary
                </h3>
                
                <div className="bg-white rounded-lg p-4 shadow-sm space-y-3">
                  <div className="flex justify-between items-center text-gray-600">
                    <span>Total Items:</span>
                    <span className="font-semibold">{cart.reduce((sum, item) => sum + item.quantity, 0)} items</span>
                  </div>
                  <div className="flex justify-between items-center text-gray-600">
                    <span>Order Total:</span>
                    <span className="font-semibold">EGP {getTotalPrice().toFixed(2)}</span>
                  </div>
                  <hr className="border-gray-200" />
                  <div className="flex justify-between items-center text-xl font-bold">
                    <span className="text-gray-900">Required Deposit (50%):</span>
                    <span className="text-green-600 bg-green-100 px-3 py-1 rounded-lg">EGP {depositAmount}</span>
                  </div>
                </div>
              </div>

              {/* Payment Instructions */}
              <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
                <div className="bg-gradient-to-r from-blue-500 to-purple-500 p-4">
                  <h4 className="font-bold text-white text-lg flex items-center">
                    <Phone size={22} className="mr-3 bg-white/20 p-1 rounded" />
                    Step 1: Make Payment
                  </h4>
                </div>
                <div className="p-4 space-y-4">
                  <p className="text-gray-700 leading-relaxed">
                    Transfer your deposit <span className="font-bold text-green-600 bg-green-100 px-2 py-1 rounded">EGP {depositAmount}</span> via Vodafone Cash to:
                  </p>
                  <div className="bg-gradient-to-r from-red-50 to-orange-50 p-4 rounded-lg border border-red-200 text-center">
                    <div className="text-sm text-gray-600 mb-1">Vodafone Cash Number</div>
                    <div className="text-2xl font-bold text-gray-900 tracking-wider">01016887251</div>
                    <div className="text-sm text-red-600 mt-2">ðŸ“± Save this number</div>
                  </div>
                </div>
              </div>

              {/* Confirmation Instructions */}
              <div className="bg-white rounded-xl shadow-lg border border-gray-200 overflow-hidden">
                <div className="bg-gradient-to-r from-green-500 to-emerald-500 p-4">
                  <h4 className="font-bold text-white text-lg flex items-center">
                    <MessageCircle size={22} className="mr-3 bg-white/20 p-1 rounded" />
                    Step 2: Send Confirmation
                  </h4>
                </div>
                <div className="p-4 space-y-4">
                  <p className="text-gray-700 leading-relaxed">
                    Send a screenshot of your payment confirmation via:
                  </p>
                  <div className="grid grid-cols-2 gap-3">
                    <div className="bg-gradient-to-r from-pink-50 to-purple-50 p-3 rounded-lg border border-pink-200 text-center">
                      <Instagram size={24} className="mx-auto mb-2 text-pink-500" />
                      <div className="font-semibold text-gray-800">Instagram</div>
                      <div className="text-sm text-gray-600">Direct Message</div>
                    </div>
                    <div className="bg-gradient-to-r from-green-50 to-emerald-50 p-3 rounded-lg border border-green-200 text-center">
                      <Phone size={24} className="mx-auto mb-2 text-green-500" />
                      <div className="font-semibold text-gray-800">WhatsApp</div>
                      <div className="text-sm text-gray-600">Chat Message</div>
                    </div>
                  </div>
                  <div className="bg-gradient-to-r from-gray-50 to-gray-100 p-4 rounded-lg border border-gray-200 text-center">
                    <div className="text-sm text-gray-600 mb-1">Contact Number</div>
                    <div className="text-xl font-bold text-gray-900">01016887251</div>
                  </div>
                </div>
              </div>

              {/* Final Step */}
              <div className="bg-gradient-to-r from-purple-50 to-blue-50 p-5 rounded-xl border border-purple-200">
                <div className="flex items-start space-x-3">
                  <div className="text-2xl">ðŸ“ž</div>
                  <div>
                    <h5 className="font-bold text-gray-900 mb-2">Final Step - We'll Contact You!</h5>
                    <p className="text-gray-700 text-sm leading-relaxed">
                      Once we receive your payment confirmation, our team will contact you within <span className="font-semibold text-purple-600">24 hours</span> to finalize your order details and arrange delivery.
                    </p>
                  </div>
                </div>
              </div>
            </div>

            {/* Action Buttons */}
            <div className="flex space-x-4 mt-8">
              <button
                onClick={() => setShowInstructions(false)}
                className="flex-1 bg-gray-200 hover:bg-gray-300 text-gray-800 py-4 px-6 font-semibold tracking-wide uppercase transition-all duration-300 rounded-xl transform hover:scale-[1.02]"
              >
                â† Back to Cart
              </button>
              <button
                onClick={handleGotIt}
                className="flex-1 bg-gradient-to-r from-green-500 to-emerald-500 hover:from-green-600 hover:to-emerald-600 text-white py-4 px-6 font-semibold tracking-wide uppercase transition-all duration-300 rounded-xl shadow-lg hover:shadow-xl transform hover:scale-[1.02]"
              >
                Perfect! Got It âœ¨
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartModal;
