import React, { useState } from 'react';
import { X, Check, AlertCircle } from 'lucide-react';

const CheckoutModal = ({ customerInfo, setCustomerInfo, cart, getTotalPrice, onClose, onSubmit }) => {
  const [showResult, setShowResult] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    setIsLoading(true);
    setErrorMessage('');
    setShowResult(null);
    
    try {
      // حفظ الطلب محلياً أولاً
      const orderData = {
        customerInfo,
        items: cart,
        total: getTotalPrice(),
        date: new Date().toISOString(),
        status: 'pending'
      };
      
      // حفظ في localStorage
      const existingOrders = JSON.parse(localStorage.getItem('sk_orders') || '[]');
      const newOrder = {
        ...orderData,
        id: Date.now() // استخدام الطوابع الزمنية كمعرف فريد
      };
      
      localStorage.setItem('sk_orders', JSON.stringify([...existingOrders, newOrder]));
      
      console.log('✅ Order saved locally:', newOrder);
      
      // محاولة الإرسال إلى السيرفر
      let serverResult = null;
      try {
        if (navigator.onLine) {
          console.log('📡 Online, attempting to submit to server...');
          serverResult = await onSubmit();
          console.log('✅ Server submission result:', serverResult);
        } else {
          console.log('📴 Offline, order saved locally only');
        }
      } catch (serverError) {
        console.warn('⚠️ Server submission failed, but order saved locally:', serverError);
        // لا نرمي خطأ هنا لأن الطلب حفظ محلياً بنجاح
      }
      
      // اعتبار الطلب ناجحاً إذا حفظ محلياً، بغض النظر عن حالة السيرفر
      setShowResult('success');
      
    } catch (error) {
      console.error('💥 Error in order processing:', error);
      
      let errorMsg = 'An error occurred while processing your order';
      
      if (error && error.message) {
        errorMsg = error.message;
      }
      
      setErrorMessage(errorMsg);
      setShowResult('failure');
      
    } finally {
      setIsLoading(false);
    }
  };

  const resetAndTryAgain = () => {
    setShowResult(null);
    setErrorMessage('');
    setIsLoading(false);
  };

  const closeModal = () => {
    setShowResult(null);
    setErrorMessage('');
    setIsLoading(false);
    onClose();
  };

  // Success Screen
  if (showResult === 'success') {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
        <div className="bg-white max-w-md rounded-lg shadow-2xl p-8 text-center">
          <div className="w-20 h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-6">
            <Check className="w-10 h-10 text-green-600" />
          </div>
          <h3 className="text-2xl font-bold text-gray-900 mb-4">Order Received Successfully!</h3>
          <p className="text-gray-600 mb-6">
            Thank you for your order. We have received your request and will contact you shortly to confirm the details.
          </p>
          
          <div className="bg-blue-50 p-4 rounded-lg border border-blue-200 mb-4">
            <p className="text-blue-700 text-sm font-medium">
              📋 Order Summary:
            </p>
            <div className="mt-2 text-xs text-blue-600 text-left">
              {cart.map((item, index) => (
                <div key={index} className="flex justify-between">
                  <span>{item.name} × {item.quantity}</span>
                  <span>EGP {(item.price * item.quantity).toFixed(2)}</span>
                </div>
              ))}
              <div className="border-t border-blue-200 pt-1 mt-1 font-bold">
                <div className="flex justify-between">
                  <span>Total:</span>
                  <span>EGP {getTotalPrice().toFixed(2)}</span>
                </div>
              </div>
            </div>
          </div>
          
          <div className="bg-green-50 p-4 rounded-lg border border-green-200 mb-6">
            <p className="text-green-700 text-sm">
              Please transfer the deposit 50% to <span className="font-bold">01016887251</span> via Vodafone Cash to confirm your order
            </p>
          </div>
          
          <button
            onClick={closeModal}
            className="bg-green-600 text-white px-6 py-3 rounded-lg hover:bg-green-700 transition-colors font-medium"
          >
            Close
          </button>
        </div>
      </div>
    );
  }

  // Failure Screen
  if (showResult === 'failure') {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
        <div className="bg-white max-w-md rounded-lg shadow-2xl p-8 text-center">
          <div className="w-20 h-20 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-6">
            <AlertCircle className="w-10 h-10 text-red-600" />
          </div>
          <h3 className="text-2xl font-bold text-gray-900 mb-4">Order Failed</h3>
          <p className="text-gray-600 mb-6">{errorMessage || 'Unknown error occurred'}</p>
          <div className="bg-red-50 p-4 rounded-lg border border-red-200 mb-6">
            <p className="text-red-700 text-sm">
              Please check your information and try again, or contact us directly.
            </p>
          </div>
          <div className="flex gap-3 justify-center">
            <button
              onClick={resetAndTryAgain}
              className="bg-red-600 text-white px-6 py-3 rounded hover:bg-red-700 transition-colors"
            >
              Try Again
            </button>
            <button
              onClick={closeModal}
              className="bg-gray-500 text-white px-6 py-3 rounded hover:bg-gray-600 transition-colors"
            >
              Cancel
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Loading Screen
  if (isLoading) {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
        <div className="bg-white max-w-md rounded-lg shadow-2xl p-8 text-center">
          <div className="flex flex-col items-center">
            <div className="w-16 h-16 border-4 border-blue-500 border-t-transparent rounded-full animate-spin mb-4"></div>
            <h3 className="text-xl font-bold text-gray-900 mb-2">Processing Your Order</h3>
            <p className="text-gray-600">Please wait while we process your order...</p>
          </div>
        </div>
      </div>
    );
  }

  // Main Checkout Form
  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-white w-full max-w-lg max-h-[90vh] overflow-y-auto shadow-2xl rounded-lg">
        <div className="p-6 border-b border-gray-200 sticky top-0 bg-white z-10">
          <div className="flex justify-between items-center">
            <h2 className="text-2xl font-bold text-gray-900">Checkout</h2>
            <button 
              onClick={onClose} 
              className="text-gray-500 hover:text-gray-700 transition-colors"
            >
              <X className="w-6 h-6" />
            </button>
          </div>
        </div>
        
        <div className="p-6">
          <form onSubmit={handleSubmit}>
            <div className="space-y-4 mb-6">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Full Name</label>
                <input
                  type="text"
                  placeholder="Enter your full name"
                  value={customerInfo.name}
                  onChange={(e) => setCustomerInfo({...customerInfo, name: e.target.value})}
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                  disabled={isLoading}
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Email Address</label>
                <input
                  type="email"
                  placeholder="Enter your email"
                  value={customerInfo.email}
                  onChange={(e) => setCustomerInfo({...customerInfo, email: e.target.value})}
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                  disabled={isLoading}
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Phone Number</label>
                <input
                  type="tel"
                  placeholder="Enter your phone number"
                  value={customerInfo.phone}
                  onChange={(e) => setCustomerInfo({...customerInfo, phone: e.target.value})}
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  required
                  disabled={isLoading}
                />
              </div>
              
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-1">Delivery Address</label>
                <textarea
                  placeholder="Enter your delivery address"
                  value={customerInfo.address}
                  onChange={(e) => setCustomerInfo({...customerInfo, address: e.target.value})}
                  className="w-full p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent h-24 resize-none"
                  required
                  disabled={isLoading}
                />
              </div>
            </div>
            
            <div className="mb-6">
              <h3 className="font-semibold text-lg mb-3 text-gray-900">Order Summary</h3>
              <div className="bg-gray-50 p-4 rounded-lg border border-gray-200">
                {cart.map((item) => (
                  <div key={item.id} className="flex justify-between py-2 text-sm text-gray-600">
                    <span>{item.name} × {item.quantity}</span>
                    <span>EGP {(item.price * item.quantity).toFixed(2)}</span>
                  </div>
                ))}
                <div className="border-t pt-2 mt-2 border-gray-200">
                  <div className="flex justify-between font-bold text-lg text-gray-900">
                    <span>Total</span>
                    <span>EGP {getTotalPrice().toFixed(2)}</span>
                  </div>
                </div>
              </div>
            </div>
            
            <button
              type="submit"
              disabled={isLoading}
              className={`w-full py-3 font-medium rounded-lg transition-all duration-300 ${
                isLoading 
                  ? 'bg-gray-400 text-white cursor-not-allowed' 
                  : 'bg-blue-600 text-white hover:bg-blue-700 shadow-md hover:shadow-lg'
              }`}
            >
              {isLoading ? (
                <div className="flex items-center justify-center">
                  <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                  Processing...
                </div>
              ) : (
                'Place Order'
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CheckoutModal;
