import React, { useState } from 'react';
import { X, Check, AlertCircle } from 'lucide-react';

const CheckoutModal = ({ customerInfo, setCustomerInfo, cart, getTotalPrice, onClose, onSubmit }) => {
  const [showResult, setShowResult] = useState(null); // null, 'success', or 'failure'
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage('');
    setShowResult(null); // تأكد من إخفاء أي نتائج سابقة
    
    try {
      console.log('Starting order submission...');
      
      // استدعاء onSubmit والانتظار للنتيجة
      const result = await onSubmit();
      
      console.log('Order submission result:', result);
      
      // التحقق الصارم من النجاح
      if (result && (result.id || result.success === true)) {
        console.log('Order confirmed as successful');
        setShowResult('success');
        
        // إغلاق الـ modal بعد 4 ثوانٍ
        setTimeout(() => {
          onClose();
        }, 4000);
      } else {
        console.log('Order result is invalid:', result);
        throw new Error('لم يتم تأكيد الطلب من السيرفر');
      }
      
    } catch (error) {
      console.error('Order submission error:', error);
      
      // Determine error message based on error type
      let errorMsg = 'Error submitting order';
      
      if (error && error.message) {
        if (error.message.includes('Failed to fetch') || 
            error.message.includes('NetworkError') || 
            error.message.includes('network') ||
            !navigator.onLine) {
          errorMsg = 'No internet connection. Please check your network and try again';
        } else if (error.message.includes('500') || error.message.includes('server')) {
          errorMsg = 'Server error. Please try again later';
        } else {
          errorMsg = error.message;
        }
      }
      
      setErrorMessage(errorMsg);
      setShowResult('failure');
    } finally {
      setIsLoading(false);
    }
  };

  const resetAndTryAgain = () => {
    setShowResult(null);
    setErrorMessage('');
    setIsLoading(false);
  };

  const closeModal = () => {
    setShowResult(null);
    setErrorMessage('');
    setIsLoading(false);
    onClose();
  };

  // Success Screen
  if (showResult === 'success') {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
        <div className="bg-white max-w-md rounded-lg shadow-2xl p-8 text-center">
          <div className="w-20 h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-6">
            <Check className="w-10 h-10 text-green-600" />
          </div>
          <h3 className="text-2xl font-bold text-gray-900 mb-4">تم إرسال الطلب بنجاح!</h3>
          <p className="text-gray-600 mb-6">
            شكراً لك على طلبك. سنتواصل معك قريباً لتأكيد التفاصيل.
          </p>
          <div className="bg-green-50 p-4 rounded-lg border border-green-200 mb-6">
            <p className="text-green-700 text-sm">
              يرجى تحويل العربون 50% على رقم <span className="font-bold">01016887251</span> فودافون كاش لتأكيد الطلب
            </p>
          </div>
          <button
            onClick={closeModal}
            className="bg-green-600 text-white px-6 py-3 rounded hover:bg-green-700 transition-colors"
          >
            إغلاق
          </button>
        </div>
      </div>
    );
  }

  // Failure Screen
  if (showResult === 'failure') {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
        <div className="bg-white max-w-md rounded-lg shadow-2xl p-8 text-center">
          <div className="w-20 h-20 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-6">
            <AlertCircle className="w-10 h-10 text-red-600" />
          </div>
          <h3 className="text-2xl font-bold text-gray-900 mb-4">فشل في إرسال الطلب</h3>
          <p className="text-gray-600 mb-6">{errorMessage}</p>
          <div className="flex gap-3 justify-center">
            <button
              onClick={resetAndTryAgain}
              className="bg-red-600 text-white px-6 py-3 rounded hover:bg-red-700 transition-colors"
            >
              حاول مرة أخرى
            </button>
            <button
              onClick={closeModal}
              className="bg-gray-500 text-white px-6 py-3 rounded hover:bg-gray-600 transition-colors"
            >
              إلغاء
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Main Checkout Form
  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4">
      <div className="bg-white w-full max-w-lg max-h-[90vh] overflow-y-auto shadow-2xl">
        <div className="p-6 border-b border-gray-200">
          <div className="flex justify-between items-center">
            <h2 className="text-2xl font-bold text-gray-900">إتمام الطلب</h2>
            <button onClick={onClose} className="text-gray-500 hover:text-gray-700 text-2xl">×</button>
          </div>
        </div>
        
        <div className="p-6">
          <form onSubmit={handleSubmit}>
            <div className="space-y-4 mb-6">
              <input
                type="text"
                placeholder="الاسم الكامل"
                value={customerInfo.name}
                onChange={(e) => setCustomerInfo({...customerInfo, name: e.target.value})}
                className="w-full p-3 bg-gray-50 border border-gray-200 rounded focus:outline-none focus:ring-2 focus:ring-gray-400"
                required
                disabled={isLoading}
              />
              <input
                type="email"
                placeholder="البريد الإلكتروني"
                value={customerInfo.email}
                onChange={(e) => setCustomerInfo({...customerInfo, email: e.target.value})}
                className="w-full p-3 bg-gray-50 border border-gray-200 rounded focus:outline-none focus:ring-2 focus:ring-gray-400"
                required
                disabled={isLoading}
              />
              <input
                type="tel"
                placeholder="رقم الهاتف"
                value={customerInfo.phone}
                onChange={(e) => setCustomerInfo({...customerInfo, phone: e.target.value})}
                className="w-full p-3 bg-gray-50 border border-gray-200 rounded focus:outline-none focus:ring-2 focus:ring-gray-400"
                required
                disabled={isLoading}
              />
              <textarea
                placeholder="عنوان التسليم"
                value={customerInfo.address}
                onChange={(e) => setCustomerInfo({...customerInfo, address: e.target.value})}
                className="w-full p-3 bg-gray-50 border border-gray-200 rounded focus:outline-none focus:ring-2 focus:ring-gray-400 h-24 resize-none"
                required
                disabled={isLoading}
              />
            </div>
            
            <div className="mb-6">
              <h3 className="font-semibold mb-3 text-gray-900">ملخص الطلب</h3>
              <div className="bg-gray-50 p-4 rounded-lg">
                {cart.map((item) => (
                  <div key={item.id} className="flex justify-between py-2 text-sm text-gray-600">
                    <span>{item.name} × {item.quantity}</span>
                    <span>{(item.price * item.quantity).toFixed(2)} جنيه</span>
                  </div>
                ))}
                <div className="border-t pt-2 mt-2 border-gray-200">
                  <div className="flex justify-between font-bold text-lg text-gray-900">
                    <span>الإجمالي</span>
                    <span>{getTotalPrice().toFixed(2)} جنيه</span>
                  </div>
                </div>
              </div>
            </div>
            
            <button
              type="submit"
              disabled={isLoading}
              className={`w-full py-3 font-medium tracking-wide uppercase transition-all duration-300 rounded ${
                isLoading 
                  ? 'bg-gray-400 text-white cursor-not-allowed' 
                  : 'bg-black text-white hover:bg-gray-800'
              }`}
            >
              {isLoading ? (
                <div className="flex items-center justify-center">
                  <div className="w-5 h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                  جاري الإرسال...
                </div>
              ) : (
                'إرسال الطلب'
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CheckoutModal;
