import React, { useEffect, useState } from 'react';
import { X, Plus, Minus, Trash2, ChevronDown, ChevronUp } from 'lucide-react';

const CartModal = ({ cart, updateQuantity, removeFromCart, getTotalPrice, onClose, onCheckout }) => {
  const [showInstructions, setShowInstructions] = useState(false);
  
  useEffect(() => {
    document.body.style.overflow = 'hidden';
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, []);

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4 animate-fade-in">
      <div className="bg-white w-full max-w-2xl max-h-[80vh] overflow-hidden shadow-2xl animate-slide-up">
        <div className="p-6 border-b border-gray-200">
          <div className="flex justify-between items-center">
            <h2 className="text-2xl font-bold text-gray-900 tracking-wide">Your Cart</h2>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700 text-2xl transition-colors duration-200"
            >
              ×
            </button>
          </div>
        </div>
        
        <div className="p-6 overflow-y-auto max-h-96">
          {cart.length === 0 ? (
            <div className="text-center py-12">
              <div className="text-6xl mb-4 text-gray-300">🛒</div>
              <p className="text-gray-500 text-lg">Your cart is empty</p>
            </div>
          ) : (
            <div className="space-y-4">
              {cart.map((item) => {
                // تحديد مصدر الصورة بشكل صحيح
                const fallbackImage = 'https://via.placeholder.com/100x100?text=No+Image';
                const firstImage = Array.isArray(item.images) && item.images.length > 0 ? item.images[0] : undefined;
                const imageSrc = item.image_url || firstImage || fallbackImage;
                
                return (
                  <div key={item.id} className="flex items-center space-x-4 p-4 bg-gray-50 hover:bg-gray-100 transition-colors duration-200">
                    <img 
                      src={imageSrc} 
                      alt={item.name}
                      onError={(e) => {
                        e.target.src = fallbackImage;
                      }}
                      className="w-16 h-16 object-cover rounded"
                    />
                    <div className="flex-1">
                      <h3 className="font-medium text-gray-900">{item.name}</h3>
                      <p className="text-gray-600 text-sm">${item.price}</p>
                    </div>
                    <div className="flex items-center space-x-2">
                      <button
                        onClick={() => updateQuantity(item.id, item.quantity - 1)}
                        className="w-8 h-8 bg-gray-200 hover:bg-gray-300 transition-colors duration-200 flex items-center justify-center"
                      >
                        -
                      </button>
                      <span className="w-12 text-center font-medium">{item.quantity}</span>
                      <button
                        onClick={() => updateQuantity(item.id, item.quantity + 1)}
                        className="w-8 h-8 bg-gray-200 hover:bg-gray-300 transition-colors duration-200 flex items-center justify-center"
                      >
                        +
                      </button>
                    </div>
                    <button
                      onClick={() => removeFromCart(item.id)}
                      className="text-red-500 hover:text-red-700 transition-colors duration-200"
                    >
                      <Trash2 size={16} />
                    </button>
                  </div>
                );
              })}
            </div>
          )}
        </div>
        
        {cart.length > 0 && (
          <div className="p-6 border-t border-gray-200 bg-gray-50">
            {/* Order Confirmation Instructions */}
            <div className="mb-6 bg-blue-50 border border-blue-200 rounded-lg p-4">
              <div 
                className="flex justify-between items-center cursor-pointer"
                onClick={() => setShowInstructions(!showInstructions)}
              >
                <h3 className="text-lg font-semibold text-blue-800">Order Confirmation Instructions</h3>
                {showInstructions ? <ChevronUp size={20} className="text-blue-600" /> : <ChevronDown size={20} className="text-blue-600" />}
              </div>
              
              {showInstructions && (
                <div className="mt-3 text-blue-900">
                  <p className="mb-3 font-medium">To confirm your order, kindly follow the steps below:</p>
                  <ol className="list-decimal pl-5 space-y-2">
                    <li>Please transfer the deposit (50% of the total amount) via Vodafone Cash to <span className="font-bold">01016887251</span>.</li>
                    <li>Kindly send a screenshot of the payment confirmation via Instagram Direct Message or WhatsApp to <span className="font-bold">01016887251</span>.</li>
                  </ol>
                  <p className="mt-3 font-medium">Once completed, our team will contact you to finalize your order, secure your reservation, and arrange the delivery details.</p>
                </div>
              )}
            </div>
            
            <div className="flex justify-between items-center mb-4">
              <span className="text-xl font-bold text-gray-900">Total: ${getTotalPrice().toFixed(2)}</span>
            </div>
            <button
              onClick={onCheckout}
              className="w-full bg-black text-white py-3 font-medium tracking-wide uppercase transition-all duration-300 hover:bg-gray-800 transform hover:scale-[1.02]"
            >
              Proceed to Checkout
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartModal;
