import React, { useState, useEffect } from 'react';
import { ShoppingBag, Search, User, ShoppingCart, Menu, X } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const navItems = [
  { name: "Home", href: "#home", scrollTo: "home" },
  { name: "Products", href: "#products", scrollTo: "products" },
  { name: "About", href: "#about", scrollTo: "about" },
  { name: "Contact", href: "#contact", scrollTo: "contact" },
];

const listVariants = {
  hidden: {},
  visible: {
    transition: {
      staggerChildren: 0.15,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: { duration: 0.4, ease: "easeOut" }
  },
};

const Header = ({ cartCount = 0, toggleCart = () => {}, toggleMenu = () => {} }) => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [scrollY, setScrollY] = useState(0);

  // تأثير عند التمرير
  useEffect(() => {
    const handleScroll = () => {
      setScrollY(window.scrollY);
    };
    
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // حساب الشفافية بناءً على مسافة السكرول
  const getBackgroundOpacity = () => {
    const scrollThreshold = 100; // النقطة التي تبدأ عندها الشفافية تتغير
    const maxScroll = 300; // النقطة التي تصل عندها للشفافية القصوى
    
    if (scrollY <= scrollThreshold) {
      return 0.95; // الشفافية الأولية
    } else if (scrollY >= maxScroll) {
      return 0.3; // أقصى شفافية
    } else {
      // تدرج سلس بين القيم
      const progress = (scrollY - scrollThreshold) / (maxScroll - scrollThreshold);
      return 0.95 - (progress * 0.65); // من 0.95 إلى 0.3
    }
  };

  // حساب حجم البلر
  const getBackdropBlur = () => {
    return scrollY > 50 ? 'backdrop-blur-lg' : 'backdrop-blur-sm';
  };

  // حساب ارتفاع الهيدر
  const getHeaderPadding = () => {
    return scrollY > 50 ? 'py-2' : 'py-4';
  };

  // دالة للانتقال السلس للأقسام
  const scrollToSection = (sectionId) => {
    if (sectionId === 'home') {
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    } else {
      const element = document.getElementById(sectionId);
      if (element) {
        const headerHeight = 80;
        const elementPosition = element.offsetTop - headerHeight;
        
        window.scrollTo({
          top: elementPosition,
          behavior: 'smooth'
        });
      }
    }
  };

  return (
    <motion.header
      initial={{ y: -80, opacity: 0 }}
      animate={{ 
        y: 0, 
        opacity: 1,
        backgroundColor: `rgba(0, 0, 0, ${getBackgroundOpacity()})`,
      }}
      transition={{ 
        y: { duration: 0.6, ease: "easeOut" },
        opacity: { duration: 0.6, ease: "easeOut" },
        backgroundColor: { duration: 0.3, ease: "easeInOut" }
      }}
      className={`${getBackdropBlur()} border-b border-gray-800/50 sticky top-0 z-50 transition-all duration-300 ${getHeaderPadding()}`}
      style={{
        boxShadow: scrollY > 10 
          ? `0 20px 25px -5px rgba(0, 0, 0, ${0.1 + (scrollY / 1000)}), 0 10px 10px -5px rgba(0, 0, 0, ${0.04 + (scrollY / 2000)})` 
          : '0 10px 15px -3px rgba(0, 0, 0, 0.1)'
      }}
    >
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center">
          {/* الشعار */}
          <motion.div 
            className="flex items-center space-x-3 space-x-reverse cursor-pointer"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 300 }}
            onClick={() => scrollToSection('home')}
          >
            <div className="relative">
              <motion.div
                animate={{ 
                  scale: scrollY > 50 ? 0.9 : 1,
                }}
                transition={{ duration: 0.3 }}
              >
                <ShoppingBag className="w-8 h-8 text-white" />
              </motion.div>
              <motion.div 
                className="absolute -bottom-1 -right-1 w-2 h-2 bg-gray-400 rounded-full"
                animate={{
                  scale: [1, 1.2, 1],
                  opacity: scrollY > 100 ? 0.7 : 1
                }}
                transition={{ 
                  scale: { duration: 2, repeat: Infinity },
                  opacity: { duration: 0.3 }
                }}
              />
            </div>
            <motion.h1 
              className="text-2xl font-bold text-white"
              animate={{
                fontSize: scrollY > 50 ? '1.75rem' : '2rem'
              }}
              transition={{ duration: 0.3 }}
            >
              SK 
            </motion.h1>
          </motion.div>

          {/* قائمة التنقل */}
          <motion.nav 
            className="hidden md:flex items-center space-x-10 space-x-reverse"
            variants={listVariants}
            initial="hidden"
            animate="visible"
          >
            {navItems.map((item, index) => (
              <motion.button
                key={index}
                onClick={() => scrollToSection(item.scrollTo)}
                className="text-gray-300 hover:text-white transition-colors font-medium relative group cursor-pointer bg-transparent border-none"
                variants={itemVariants}
                whileHover={{ y: -3, scale: 1.05 }}
                animate={{
                  opacity: scrollY > 200 ? 0.8 : 1
                }}
                transition={{ opacity: { duration: 0.3 } }}
              >
                {item.name}
                <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-gradient-to-r from-white to-gray-300 transition-all group-hover:w-full"></span>
              </motion.button>
            ))}
          </motion.nav>

          {/* الأزرار */}
          <div className="flex items-center space-x-3 space-x-reverse">
            {/* البحث */}
            <div className={`relative flex items-center ${isSearchOpen ? 'w-48' : 'w-10'} transition-all`}>
              <motion.button
                whileTap={{ scale: 0.9 }}
                onClick={() => setIsSearchOpen(!isSearchOpen)}
                className="p-2 hover:bg-white/10 rounded-full transition-all absolute right-0 z-10"
                animate={{
                  backgroundColor: scrollY > 100 ? 'rgba(255, 255, 255, 0.05)' : 'transparent'
                }}
              >
                <motion.div
                  animate={{ rotate: isSearchOpen ? 180 : 0 }}
                  transition={{ duration: 0.3 }}
                >
                  {isSearchOpen ? (
                    <X className="w-5 h-5 text-gray-300" />
                  ) : (
                    <Search className="w-5 h-5 text-gray-300" />
                  )}
                </motion.div>
              </motion.button>

              <AnimatePresence>
                {isSearchOpen && (
                  <motion.input
                    key="search"
                    initial={{ width: 0, opacity: 0, scale: 0.8 }}
                    animate={{ width: "100%", opacity: 1, scale: 1 }}
                    exit={{ width: 0, opacity: 0, scale: 0.8 }}
                    transition={{ duration: 0.4, ease: "easeOut" }}
                    type="text"
                    placeholder="...Search"
                    className="h-10 pr-10 pl-3 rounded-full bg-white/10 border border-white/20 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-white/50 focus:border-white/50 text-sm backdrop-blur-md"
                    autoFocus
                  />
                )}
              </AnimatePresence>
            </div>

            {/* المستخدم */}
            <motion.button
              whileHover={{ scale: 1.1 }}
              className="p-2 hover:bg-white/10 rounded-full transition-colors hidden sm:block"
              animate={{
                backgroundColor: scrollY > 100 ? 'rgba(255, 255, 255, 0.05)' : 'transparent'
              }}
            >
              <User className="w-5 h-5 text-gray-300" />
            </motion.button>

            {/* السلة */}
            <motion.button
              whileHover={{ scale: 1.1 }}
              onClick={toggleCart}
              className="relative p-2 hover:bg-white/10 rounded-full transition-colors group"
              animate={{
                backgroundColor: scrollY > 100 ? 'rgba(255, 255, 255, 0.05)' : 'transparent'
              }}
            >
              <motion.div
                animate={{
                  rotate: cartCount > 0 ? [0, -10, 10, -10, 0] : 0
                }}
                transition={{ duration: 0.5 }}
              >
                <ShoppingCart className="w-5 h-5 text-gray-300 group-hover:text-white transition-colors" />
              </motion.div>
              <AnimatePresence>
                {cartCount > 0 && (
                  <motion.span
                    initial={{ scale: 0, opacity: 0 }}
                    animate={{ scale: 1, opacity: 1 }}
                    exit={{ scale: 0, opacity: 0 }}
                    transition={{ type: "spring", stiffness: 200 }}
                    className="absolute -top-1 -right-1 bg-gradient-to-r from-white to-gray-100 text-black text-xs rounded-full w-5 h-5 flex items-center justify-center font-semibold shadow-lg"
                  >
                    <motion.span
                      animate={{ scale: [1, 1.1, 1] }}
                      transition={{ duration: 1, repeat: Infinity }}
                    >
                      {cartCount}
                    </motion.span>
                  </motion.span>
                )}
              </AnimatePresence>
            </motion.button>

            {/* المنيو للموبايل */}
            <motion.button
              whileHover={{ rotate: 90 }}
              onClick={toggleMenu}
              className="md:hidden p-2 hover:bg-white/10 rounded-full transition-colors ml-2"
              animate={{
                backgroundColor: scrollY > 100 ? 'rgba(255, 255, 255, 0.05)' : 'transparent'
              }}
            >
              <Menu className="w-5 h-5 text-gray-300" />
            </motion.button>
          </div>
        </div>
      </div>

      {/* خط متدرج في الأسفل يظهر عند السكرول */}
      <motion.div
        className="absolute bottom-0 left-0 right-0 h-px bg-gradient-to-r from-transparent via-white/30 to-transparent"
        animate={{
          opacity: scrollY > 50 ? 1 : 0,
          scaleX: scrollY > 50 ? 1 : 0
        }}
        transition={{ duration: 0.3 }}
      />
    </motion.header>
  );
};

export default Header;
