import React, { useState, useEffect, useRef } from 'react';
import Header from './components/Header';
import ProductCard from './components/ProductCard';
import CartModal from './components/CartModal';
import CheckoutModal from './components/CheckoutModal';
import ProductModal from './components/ProductModal';
import Footer from './components/Footer';
import { apiService } from './services/api';

const MainApp = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);
  const [showCart, setShowCart] = useState(false);
  const [showCheckout, setShowCheckout] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    email: '',
    phone: '',
    address: ''
  });
  const [isLoading, setIsLoading] = useState(true);
  
  const productsRef = useRef(null);
  const aboutRef = useRef(null);
  const contactRef = useRef(null);

  // Fetch products on component mount
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setIsLoading(true);
        const productsData = await apiService.getProducts();
        setProducts(productsData);
      } catch (error) {
        console.error('Error fetching products:', error);
        // Fallback to dummy data
        setProducts([
          {
            id: 1,
            name: "شنطة جلد طبيعي",
            description: "شنطة أنيقة مصنوعة من الجلد الطبيعي عالي الجودة",
            price: 299.99,
            image_url: "https://images.unsplash.com/photo-1548036328-c9fa89d128fa?w=400",
            category: "شنط يد",
            stock_quantity: 10
          },
          {
            id: 2,
            name: "شنطة كروس باج",
            description: "شنطة عملية ومريحة للاستخدام اليومي",
            price: 199.99,
            image_url: "https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400",
            category: "شنط كروس",
            stock_quantity: 15
          },
          {
            id: 3,
            name: "شنطة كلاتش",
            description: "شنطة أنيقة للمناسبات الخاصة",
            price: 399.99,
            image_url: "https://images.unsplash.com/photo-1594223274512-ad4803739b7c?w=400",
            category: "شنط كلاتش",
            stock_quantity: 8
          },
          {
            id: 4,
            name: "شنطة سفر",
            description: "شنطة متينة ومناسبة للسفر",
            price: 499.99,
            image_url: "https://images.unsplash.com/photo-1553062407-98eeb64c6a62?w=400",
            category: "شنط سفر",
            stock_quantity: 5
          },
          {
            id: 5,
            name: "شنطة ظهر",
            description: "شنطة ظهر عملية ومريحة",
            price: 249.99,
            image_url: "https://images.unsplash.com/photo-1548036328-c9fa89d128fa?w=400",
            category: "شنط ظهر",
            stock_quantity: 12
          },
          {
            id: 6,
            name: "شنطة صغيرة",
            description: "شنطة صغيرة وأنيقة للمناسبات",
            price: 179.99,
            image_url: "https://images.unsplash.com/photo-1594223274512-ad4803739b7c?w=400",
            category: "شنط صغيرة",
            stock_quantity: 7
          }
        ]);
      } finally {
        setIsLoading(false);
      }
    };

    fetchProducts();
  }, []);

  const addToCart = (product) => {
    setCart(prevCart => {
      const existingItem = prevCart.find(item => item.id === product.id);
      if (existingItem) {
        return prevCart.map(item =>
          item.id === product.id
            ? { ...item, quantity: item.quantity + 1 }
            : item
        );
      } else {
        return [...prevCart, { ...product, quantity: 1 }];
      }
    });
  };

  const updateQuantity = (productId, newQuantity) => {
    if (newQuantity <= 0) {
      removeFromCart(productId);
      return;
    }
    
    setCart(prevCart =>
      prevCart.map(item =>
        item.id === productId
          ? { ...item, quantity: newQuantity }
          : item
      )
    );
  };

  const removeFromCart = (productId) => {
    setCart(prevCart => prevCart.filter(item => item.id !== productId));
  };

  const getTotalPrice = () => {
    return cart.reduce((total, item) => total + (item.price * item.quantity), 0);
  };

  const handleCheckout = async () => {
    try {
      const orderData = {
        customer_info: customerInfo,
        items: cart.map(item => ({
          product_id: item.id,
          quantity: item.quantity,
          price: item.price
        })),
        total_amount: getTotalPrice()
      };

      await apiService.createOrder(orderData);
      
      // Clear cart and customer info
      setCart([]);
      setCustomerInfo({ name: '', email: '', phone: '', address: '' });
      setShowCheckout(false);
      setShowCart(false);
      
      alert('تم إرسال طلبك بنجاح! سنتواصل معك قريباً.');
    } catch (error) {
      console.error('Error creating order:', error);
      alert('حدث خطأ أثناء إرسال الطلب. يرجى المحاولة مرة أخرى.');
    }
  };

  const cartCount = cart.reduce((total, item) => total + item.quantity, 0);

  return (
    <div className="min-h-screen bg-white">
      <Header
        cartCount={cartCount}
        toggleCart={() => setShowCart(true)}
        scrollToProducts={() => productsRef.current?.scrollIntoView({ behavior: 'smooth' })}
        scrollToAbout={() => aboutRef.current?.scrollIntoView({ behavior: 'smooth' })}
        scrollToContact={() => contactRef.current?.scrollIntoView({ behavior: 'smooth' })}
      />

      {/* Hero Section */}
      <section className="relative min-h-screen bg-gradient-to-br from-gray-50 via-white to-gray-100 flex items-center overflow-hidden">
        {/* Background Pattern */}
        <div className="absolute inset-0 opacity-5">
          <div className="absolute top-20 left-20 w-96 h-96 border border-gray-400 rounded-full animate-pulse-slow"></div>
          <div className="absolute bottom-20 right-20 w-64 h-64 border border-gray-400 animate-pulse-slow delay-1000"></div>
          <div className="absolute top-1/3 right-1/4 w-32 h-32 border border-gray-400 rotate-45 animate-pulse-slow delay-2000"></div>
        </div>

        <div className="container mx-auto px-4 py-20">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 items-center">
            {/* Text Content */}
            <div className="text-center lg:text-right order-2 lg:order-1 animate-fade-in-up">
              <h1 className="text-6xl lg:text-7xl font-light mb-6 leading-tight">
                <span className="block text-black font-bold">شنط</span>
                <span className="block text-gray-600 font-light">يدوية</span>
                <span className="block text-black font-bold border-b-4 border-black pb-2 inline-block">فاخرة</span>
              </h1>
              
              <p className="text-xl text-gray-600 mb-8 max-w-lg leading-relaxed">
                شنط يدوية فاخرة مصنوعة بعناية فائقة وتفانٍ من قبل حرفيين مهرة، باستخدام أفضل المواد وجودة التصنيع.
              </p>
              
              <div className="flex flex-col sm:flex-row gap-4 justify-center lg:justify-start">
                <button
                  onClick={() => productsRef.current?.scrollIntoView({ behavior: 'smooth' })}
                  className="bg-black text-white px-8 py-4 font-medium tracking-wider uppercase transition-all duration-500 hover:bg-gray-800 transform hover:-translate-y-1 hover:shadow-xl group relative overflow-hidden"
                >
                  <span className="relative z-10">اكتشف المجموعة</span>
                  <div className="absolute inset-0 bg-gray-700 transform -translate-x-full group-hover:translate-x-0 transition-transform duration-500"></div>
                </button>
                
                <button 
                  onClick={() => aboutRef.current?.scrollIntoView({ behavior: 'smooth' })}
                  className="border-2 border-gray-300 text-gray-700 px-8 py-4 font-medium tracking-wider uppercase transition-all duration-500 hover:border-black hover:text-black transform hover:-translate-y-1 hover:shadow-xl"
                >
                  قصتنا
                </button>
              </div>
            </div>

            {/* Hero Image */}
            <div className="flex justify-center lg:justify-end order-1 lg:order-2 animate-fade-in-up delay-300">
              <div className="relative">
                <div className="absolute inset-0 bg-black/10 blur-3xl transform translate-x-4 translate-y-4"></div>
                <img
                  src="https://images.unsplash.com/photo-1584917865442-de89df76afd3?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&h=800&q=80"
                  alt="شنطة يدوية فاخرة"
                  className="relative w-96 h-96 object-cover shadow-2xl transform hover:scale-105 transition-all duration-700 hover:rotate-1"
                />
                
                <div className="absolute -top-6 -right-6 bg-black text-white px-4 py-2 text-sm font-bold tracking-wide uppercase shadow-lg animate-bounce-gentle">
                  جودة فائقة
                </div>
                
                <div className="absolute -bottom-6 -left-6 bg-white text-black px-4 py-2 text-sm font-bold tracking-wide uppercase shadow-lg border-2 border-gray-200 animate-bounce-gentle delay-500">
                  صناعة يدوية
                </div>
              </div>
            </div>
          </div>
        </div>

        {/* Scroll Indicator */}
        <div className="absolute bottom-8 left-1/2 transform -translate-x-1/2 animate-bounce">
          <div className="w-6 h-10 border-2 border-gray-400 rounded-full flex justify-center">
            <div className="w-1 h-3 bg-gray-400 rounded-full mt-2 animate-scroll-indicator"></div>
          </div>
        </div>
      </section>

      {/* Products Section */}
      <section ref={productsRef} className="py-20 bg-gray-50">
        <div className="container mx-auto px-4">
          <div className="text-center mb-16 animate-fade-in-up">
            <h2 className="text-5xl font-light text-gray-900 mb-4">
              <span className="font-bold">مجموعتنا</span>
            </h2>
            <div className="w-24 h-1 bg-black mx-auto mb-6"></div>
            <p className="text-xl text-gray-600 max-w-2xl mx-auto leading-relaxed">
              اكتشف مجموعتنا المختارة بعناية من الشنط اليدوية الفاخرة، كل قطعة تحكي قصة خاصة من الإتقان والجودة.
            </p>
          </div>
          
          {isLoading ? (
            <div className="flex justify-center items-center h-64">
              <div className="relative">
                <div className="w-16 h-16 border-4 border-gray-200 border-t-black rounded-full animate-spin"></div>
                <div className="absolute inset-0 w-16 h-16 border-4 border-transparent border-b-gray-400 rounded-full animate-spin-reverse"></div>
              </div>
            </div>
          ) : (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {products.map((product, index) => (
                <div 
                  key={product.id} 
                  className="animate-fade-in-up"
                  style={{ animationDelay: `${index * 150}ms` }}
                >
                  <ProductCard
                    product={product}
                    onAddToCart={addToCart}
                    onViewDetails={() => setSelectedProduct(product)}
                  />
                </div>
              ))}
            </div>
          )}
        </div>
      </section>

      {/* About Section */}
      <section ref={aboutRef} className="py-20 bg-white">
        <div className="container mx-auto px-4">
          <div className="max-w-6xl mx-auto">
            <div className="text-center mb-16 animate-fade-in-up">
              <h2 className="text-5xl font-light text-gray-900 mb-4">
                <span className="font-bold">عن</span> حرفيتنا
              </h2>
              <div className="w-24 h-1 bg-black mx-auto mb-6"></div>
              <p className="text-xl text-gray-600 max-w-3xl mx-auto leading-relaxed">
                نحن متخصصون في صناعة الشنط اليدوية الفريدة والأنيقة. نستخدم أجود أنواع المواد
                ونطبق أحدث التقنيات لضمان الجودة العالية والتصميم المميز. كل قطعة من منتجاتنا
                تحمل بصمة فريدة من الإبداع والأناقة.
              </p>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
              <div className="text-center p-8 bg-gray-50 hover:bg-white hover:shadow-lg transition-all duration-500 transform hover:-translate-y-2 group">
                <div className="text-5xl mb-6 group-hover:scale-110 transition-transform duration-300">🎨</div>
                <h3 className="text-xl font-bold mb-4 text-gray-900">تصميم فريد</h3>
                <p className="text-gray-600 leading-relaxed">
                  تصاميمنا تحمل بصمة فنية مميزة تناسب جميع الأذواق والتفضيلات.
                </p>
              </div>
              
              <div className="text-center p-8 bg-black text-white hover:bg-gray-800 transition-all duration-500 transform hover:-translate-y-2 group">
                <div className="text-5xl mb-6 group-hover:scale-110 transition-transform duration-300">✋</div>
                <h3 className="text-xl font-bold mb-4">صناعة يدوية</h3>
                <p className="text-gray-300 leading-relaxed">
                  كل قطعة تصنع يدوياً بدقة عالية واهتمام بالتفاصيل من قبل حرفيين مهرة.
                </p>
              </div>
              
              <div className="text-center p-8 bg-gray-50 hover:bg-white hover:shadow-lg transition-all duration-500 transform hover:-translate-y-2 group">
                <div className="text-5xl mb-6 group-hover:scale-110 transition-transform duration-300">⭐</div>
                <h3 className="text-xl font-bold mb-4 text-gray-900">جودة عالية</h3>
                <p className="text-gray-600 leading-relaxed">
                  نستخدم أفضل المواد لضمان المتانة والجودة العالية في كل منتج.
                </p>
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Contact Section */}
      <section ref={contactRef} className="py-20 bg-gray-100">
        <div className="container mx-auto px-4">
          <div className="max-w-4xl mx-auto">
            <div className="text-center mb-16 animate-fade-in-up">
              <h2 className="text-5xl font-light text-gray-900 mb-4">
                <span className="font-bold">تواصل</span> معنا
              </h2>
              <div className="w-24 h-1 bg-black mx-auto mb-6"></div>
              <p className="text-xl text-gray-600 leading-relaxed">
                لديك استفسارات عن منتجاتنا؟ نحن سعداء بتواصلك معنا.
              </p>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-3 gap-8 mb-12">
              <div className="text-center p-6 bg-white hover:shadow-lg transition-all duration-300 transform hover:-translate-y-1">
                <div className="text-3xl mb-4">📧</div>
                <h3 className="text-lg font-bold mb-2 text-gray-900">البريد الإلكتروني</h3>
                <p className="text-gray-600">info@handmadebags.com</p>
              </div>
              
              <div className="text-center p-6 bg-black text-white hover:bg-gray-800 transition-all duration-300 transform hover:-translate-y-1">
                <div className="text-3xl mb-4">📱</div>
                <h3 className="text-lg font-bold mb-2">الهاتف</h3>
                <p className="text-gray-300">+20 123 456 7890</p>
              </div>
              
              <div className="text-center p-6 bg-white hover:shadow-lg transition-all duration-300 transform hover:-translate-y-1">
                <div className="text-3xl mb-4">📍</div>
                <h3 className="text-lg font-bold mb-2 text-gray-900">العنوان</h3>
                <p className="text-gray-600">القاهرة، مصر</p>
              </div>
            </div>
            
            <div className="bg-white p-8 shadow-lg">
              <h3 className="text-2xl font-bold mb-6 text-center text-gray-900">أرسل لنا رسالة</h3>
              <form className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <input 
                    type="text" 
                    placeholder="اسمك" 
                    className="p-4 bg-gray-50 border border-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent transition-all duration-200"
                  />
                  <input 
                    type="email" 
                    placeholder="بريدك الإلكتروني" 
                    className="p-4 bg-gray-50 border border-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent transition-all duration-200"
                  />
                </div>
                <textarea 
                  placeholder="رسالتك" 
                  rows="6"
                  className="w-full p-4 bg-gray-50 border border-gray-200 focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent transition-all duration-200 resize-none"
                ></textarea>
                <button 
                  type="submit"
                  className="w-full bg-black text-white py-4 font-medium tracking-wider uppercase transition-all duration-300 hover:bg-gray-800 transform hover:-translate-y-1 hover:shadow-lg"
                >
                  إرسال الرسالة
                </button>
              </form>
            </div>
          </div>
        </div>
      </section>

      <Footer />

      {/* Modals */}
      {showCart && (
        <CartModal
          cart={cart}
          updateQuantity={updateQuantity}
          removeFromCart={removeFromCart}
          getTotalPrice={getTotalPrice}
          onClose={() => setShowCart(false)}
          onCheckout={() => {
            setShowCart(false);
            setShowCheckout(true);
          }}
        />
      )}

      {showCheckout && (
        <CheckoutModal
          customerInfo={customerInfo}
          setCustomerInfo={setCustomerInfo}
          cart={cart}
          getTotalPrice={getTotalPrice}
          onClose={() => setShowCheckout(false)}
          onSubmit={handleCheckout}
        />
      )}

      {selectedProduct && (
        <ProductModal
          product={selectedProduct}
          onClose={() => setSelectedProduct(null)}
          onAddToCart={(product) => {
            addToCart(product);
            setSelectedProduct(null);
          }}
        />
      )}
      
      {/* Custom Styles */}
      <style jsx>{`
        @keyframes fadeInUp {
          from {
            opacity: 0;
            transform: translateY(30px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes slideUp {
          from {
            opacity: 0;
            transform: translateY(50px);
          }
          to {
            opacity: 1;
            transform: translateY(0);
          }
        }
        
        @keyframes fadeIn {
          from { opacity: 0; }
          to { opacity: 1; }
        }
        
        @keyframes bounceGentle {
          0%, 100% {
            transform: translateY(0);
          }
          50% {
            transform: translateY(-5px);
          }
        }
        
        @keyframes pulseFlow {
          0%, 100% {
            opacity: 0.3;
            transform: scale(1);
          }
          50% {
            opacity: 0.6;
            transform: scale(1.05);
          }
        }
        
        @keyframes scrollIndicator {
          0% { transform: translateY(0); }
          50% { transform: translateY(5px); }
          100% { transform: translateY(0); }
        }
        
        @keyframes spinReverse {
          from { transform: rotate(360deg); }
          to { transform: rotate(0deg); }
        }
        
        .animate-fade-in-up {
          animation: fadeInUp 0.8s ease-out forwards;
        }
        
        .animate-slide-up {
          animation: slideUp 0.5s ease-out forwards;
        }
        
        .animate-fade-in {
          animation: fadeIn 0.5s ease-out forwards;
        }
        
        .animate-bounce-gentle {
          animation: bounceGentle 3s ease-in-out infinite;
        }
        
        .animate-pulse-slow {
          animation: pulseFlow 4s ease-in-out infinite;
        }
        
        .animate-scroll-indicator {
          animation: scrollIndicator 2s ease-in-out infinite;
        }
        
        .animate-spin-reverse {
          animation: spinReverse 1s linear infinite;
        }
        
        .delay-300 { animation-delay: 300ms; }
        .delay-500 { animation-delay: 500ms; }
        .delay-1000 { animation-delay: 1000ms; }
        .delay-2000 { animation-delay: 2000ms; }
      `}</style>
    </div>
  );
};

export default MainApp;
