import React from 'react';
import { X, ShoppingCart } from 'lucide-react';

const ProductModal = ({ product, onClose, onAddToCart }) => {
  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[80vh] overflow-hidden">
        <div className="flex justify-between items-center p-4 border-b">
          <h2 className="text-xl font-semibold">تفاصيل المنتج</h2>
          <button onClick={onClose} className="text-gray-500 hover:text-gray-700">
            <X size={24} />
          </button>
        </div>
        
        <div className="p-4 overflow-y-auto max-h-96">
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div>
              {(() => {
                const fallback = 'https://via.placeholder.com/800x600?text=No+Image';
                const firstImage = Array.isArray(product.images) && product.images.length > 0 ? product.images[0] : undefined;
                const imageSrc = product.image_url || firstImage || fallback;
                return (
                  <img
                    src={imageSrc}
                    alt={product.name}
                    onError={(e) => { e.currentTarget.src = fallback; }}
                    className="w-full h-64 object-cover rounded-lg"
                  />
                );
              })()}
            </div>
            
            <div className="space-y-4">
              <div>
                <h3 className="text-2xl font-bold text-gray-800 mb-2">
                  {product.name}
                </h3>
                <span className="bg-purple-600 text-white px-3 py-1 rounded-full text-sm font-semibold">
                  {product.category}
                </span>
              </div>
              
              <div>
                <h4 className="text-3xl font-bold text-purple-600 mb-2">
                  {product.price} جنيه
                </h4>
                <p className="text-sm text-gray-500">
                  المخزون المتاح: {product.stock_quantity} قطعة
                </p>
              </div>
              
              <div>
                <h4 className="font-semibold text-gray-800 mb-2">الوصف:</h4>
                <p className="text-gray-600 leading-relaxed">
                  {product.description}
                </p>
              </div>
              
              <button
                onClick={() => onAddToCart(product)}
                className="w-full bg-purple-600 text-white py-3 rounded-lg hover:bg-purple-700 transition-colors flex items-center justify-center space-x-2 space-x-reverse"
              >
                <ShoppingCart size={20} />
                <span>أضف للسلة</span>
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductModal;
