import React, { useState } from 'react';
import { X, Check, AlertCircle } from 'lucide-react';

const CheckoutModal = ({ customerInfo, setCustomerInfo, cart, getTotalPrice, onClose, onSubmit }) => {
  const [showResult, setShowResult] = useState(null); // null, 'success', or 'failure'
  const [isLoading, setIsLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setErrorMessage('');
    
    try {
      // استدعاء onSubmit الأصلية (التي تستدعي API)
      const result = await onSubmit();
      
      // التحقق من وجود النتيجة والتأكد من النجاح
      if (result && (result.id || result.success !== false)) {
        setShowResult('success');
        
        // إغلاق الـ modal بعد 3 ثوانٍ من إظهار رسالة النجاح
        setTimeout(() => {
          setShowResult(null);
          onClose();
        }, 3000);
      } else {
        throw new Error('فشل في إنشاء الطلب - استجابة غير متوقعة من السيرفر');
      }
      
    } catch (error) {
      console.error('Order submission failed:', error);
      
      // تحديد رسالة الخطأ بناءً على نوع الخطأ
      let errorMsg = 'حدث خطأ أثناء إرسال الطلب. يرجى المحاولة مرة أخرى.';
      
      if (error.message) {
        if (error.message.includes('Failed to fetch') || error.message.includes('NetworkError') || !navigator.onLine) {
          errorMsg = 'تعذر الاتصال بالسيرفر. يرجى التحقق من اتصالك بالإنترنت والمحاولة مرة أخرى.';
        } else if (error.message.includes('stock') || error.message.includes('insufficient')) {
          errorMsg = 'عذراً، بعض المنتجات غير متوفرة بالكمية المطلوبة.';
        } else if (error.message.includes('500') || error.message.includes('خطأ في السيرفر')) {
          errorMsg = 'خطأ في السيرفر. يرجى المحاولة لاحقاً أو التواصل مع فريق الدعم.';
        } else if (error.message.includes('400') || error.message.includes('بيانات')) {
          errorMsg = 'بيانات الطلب غير صحيحة. يرجى التحقق من جميع الحقول والمحاولة مرة أخرى.';
        } else {
          errorMsg = error.message;
        }
      }
      
      setErrorMessage(errorMsg);
      setShowResult('failure');
    } finally {
      setIsLoading(false);
    }
  };

  const resetModal = () => {
    setShowResult(null);
    setErrorMessage('');
    setIsLoading(false);
  };

  // Success Screen
  if (showResult === 'success') {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-2 sm:p-4 animate-fade-in">
        <div className="bg-white w-full max-w-xs sm:max-w-md rounded-lg shadow-2xl animate-slide-up mx-2 sm:mx-4">
          <div className="p-6 sm:p-8 text-center">
            <div className="w-16 h-16 sm:w-20 sm:h-20 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4 animate-pulse">
              <Check className="w-8 h-8 sm:w-10 sm:h-10 text-green-600" />
            </div>
            <h3 className="text-xl sm:text-2xl font-bold text-gray-900 mb-2">Order Placed Successfully!</h3>
            <p className="text-gray-600 text-sm sm:text-base mb-4">
              Thank you for your order. We will contact you shortly to confirm the details.
            </p>
            <div className="bg-green-50 p-3 sm:p-4 rounded-lg border border-green-200">
              <p className="text-gray-700 text-xs xl:text-sm leading-relaxed">
                Please transfer the deposit 50% to <span className="font-bold text-green-600">01016887251</span> via Vodafone Cash to confirm your order 
              </p>
            </div>
            <button
              onClick={() => {
                resetModal();
                onClose();
              }}
              className="mt-4 px-4 py-2 bg-green-600 text-white rounded hover:bg-green-700 transition-colors duration-200 text-sm"
            >
              Close
            </button>
          </div>
        </div>
      </div>
    );
  }

  // Failure Screen
  if (showResult === 'failure') {
    return (
      <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-2 sm:p-4 animate-fade-in">
        <div className="bg-white w-full max-w-xs sm:max-w-md rounded-lg shadow-2xl animate-slide-up mx-2 sm:mx-4">
          <div className="p-6 sm:p-8 text-center">
            <div className="w-16 h-16 sm:w-20 sm:h-20 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <AlertCircle className="w-8 h-8 sm:w-10 sm:h-10 text-red-600" />
            </div>
            <h3 className="text-xl sm:text-2xl font-bold text-gray-900 mb-2">Order Failed</h3>
            <p className="text-gray-600 text-sm sm:text-base mb-4">
              {errorMessage}
            </p>
            <div className="bg-red-50 p-3 sm:p-4 rounded-lg border border-red-200 mb-4">
              <p className="text-red-700 text-xs xl:text-sm leading-relaxed">
                Please check your information and try again, or contact us directly.
              </p>
            </div>
            <div className="flex gap-2 justify-center">
              <button
                onClick={resetModal}
                className="px-4 py-2 bg-red-600 text-white rounded hover:bg-red-700 transition-colors duration-200 text-sm"
              >
                Try Again
              </button>
              <button
                onClick={() => {
                  resetModal();
                  onClose();
                }}
                className="px-4 py-2 bg-gray-500 text-white rounded hover:bg-gray-600 transition-colors duration-200 text-sm"
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      </div>
    );
  }

  // Main Checkout Form
  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-2 sm:p-4 animate-fade-in">
      <div className="bg-white w-full max-w-xs sm:max-w-md lg:max-w-lg max-h-[95vh] sm:max-h-[80vh] overflow-y-auto shadow-2xl animate-slide-up mx-2 sm:mx-4">
        <div className="p-4 sm:p-6 border-b border-gray-200">
          <div className="flex justify-between items-center">
            <h2 className="text-lg sm:text-xl lg:text-2xl font-bold text-gray-900 tracking-wide">Checkout</h2>
            <button onClick={onClose} className="text-gray-500 hover:text-gray-700 text-xl sm:text-2xl transition-colors duration-200">×</button>
          </div>
        </div>
        
        <div className="p-4 sm:p-6">
          <form onSubmit={handleSubmit}>
            <div className="space-y-3 sm:space-y-4 mb-4 sm:mb-6">
              <input
                type="text"
                placeholder="Full Name"
                value={customerInfo.name}
                onChange={(e) => setCustomerInfo({...customerInfo, name: e.target.value})}
                className="w-full p-2.5 sm:p-3 bg-gray-50 border border-gray-200 rounded focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent transition-all duration-200 text-sm sm:text-base"
                required
                disabled={isLoading}
              />
              <input
                type="email"
                placeholder="Email Address"
                value={customerInfo.email}
                onChange={(e) => setCustomerInfo({...customerInfo, email: e.target.value})}
                className="w-full p-2.5 sm:p-3 bg-gray-50 border border-gray-200 rounded focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent transition-all duration-200 text-sm sm:text-base"
                required
                disabled={isLoading}
              />
              <input
                type="tel"
                placeholder="Phone Number"
                value={customerInfo.phone}
                onChange={(e) => setCustomerInfo({...customerInfo, phone: e.target.value})}
                className="w-full p-2.5 sm:p-3 bg-gray-50 border border-gray-200 rounded focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent transition-all duration-200 text-sm sm:text-base"
                required
                disabled={isLoading}
              />
              <textarea
                placeholder="Delivery Address"
                value={customerInfo.address}
                onChange={(e) => setCustomerInfo({...customerInfo, address: e.target.value})}
                className="w-full p-2.5 sm:p-3 bg-gray-50 border border-gray-200 rounded focus:outline-none focus:ring-2 focus:ring-gray-400 focus:border-transparent transition-all duration-200 h-20 sm:h-24 resize-none text-sm sm:text-base"
                required
                disabled={isLoading}
              />
            </div>
            
            <div className="mb-4 sm:mb-6">
              <h3 className="font-semibold mb-2 sm:mb-3 text-gray-900 text-sm sm:text-base">Order Summary</h3>
              <div className="bg-gray-50 p-3 sm:p-4 rounded-lg">
                {cart.map((item) => (
                  <div key={item.id} className="flex justify-between py-1.5 sm:py-2 text-xs sm:text-sm text-gray-600">
                    <span>{item.name} × {item.quantity}</span>
                    <span>EGP {(item.price * item.quantity).toFixed(2)}</span>
                  </div>
                ))}
                <div className="border-t pt-2 mt-2 border-gray-200">
                  <div className="flex justify-between font-bold text-base sm:text-lg text-gray-900">
                    <span>Total</span>
                    <span>EGP {getTotalPrice().toFixed(2)}</span>
                  </div>
                </div>
              </div>
            </div>
            
            <button
              type="submit"
              disabled={isLoading}
              className={`w-full py-2.5 sm:py-3 font-medium tracking-wide uppercase transition-all duration-300 transform rounded text-sm sm:text-base ${
                isLoading 
                  ? 'bg-gray-400 text-white cursor-not-allowed' 
                  : 'bg-black text-white hover:bg-gray-800 hover:scale-[1.02]'
              }`}
            >
              {isLoading ? (
                <div className="flex items-center justify-center">
                  <div className="w-4 h-4 sm:w-5 sm:h-5 border-2 border-white border-t-transparent rounded-full animate-spin mr-2"></div>
                  Processing...
                </div>
              ) : (
                'Place Order'
              )}
            </button>
          </form>
        </div>
      </div>
    </div>
  );
};

export default CheckoutModal;
