import React, { useEffect, useState } from 'react';
import { X, Plus, Minus, Trash2, Phone, MessageCircle, Instagram } from 'lucide-react';

const CartModal = ({ cart, updateQuantity, removeFromCart, getTotalPrice, onClose, onCheckout }) => {
  const [showInstructions, setShowInstructions] = useState(false);

  useEffect(() => {
    document.body.style.overflow = 'hidden';
    return () => {
      document.body.style.overflow = 'unset';
    };
  }, []);

  const handleCheckout = () => {
    setShowInstructions(true);
  };

  const handleGotIt = () => {
    // إغلاق الـ CartModal وفتح الـ CheckoutModal
    onCheckout();
  };

  const depositAmount = (getTotalPrice() * 0.5).toFixed(2);

  return (
    <div className="fixed inset-0 bg-black/50 backdrop-blur-sm z-50 flex items-center justify-center p-4 animate-fade-in">
      <div className="bg-white w-full max-w-2xl max-h-[90vh] overflow-hidden shadow-2xl animate-slide-up">
        <div className="p-6 border-b border-gray-200">
          <div className="flex justify-between items-center">
            <h2 className="text-2xl font-bold text-gray-900 tracking-wide">
              {showInstructions ? 'Order Confirmation' : 'Your Cart'}
            </h2>
            <button
              onClick={onClose}
              className="text-gray-500 hover:text-gray-700 text-2xl transition-colors duration-200"
            >
              ×
            </button>
          </div>
        </div>
        
        {!showInstructions ? (
          <>
            <div className="p-6 overflow-y-auto max-h-96">
              {cart.length === 0 ? (
                <div className="text-center py-12">
                  <div className="text-6xl mb-4 text-gray-300">🛒</div>
                  <p className="text-gray-500 text-lg">Your cart is empty</p>
                </div>
              ) : (
                <div className="space-y-4">
                  {cart.map((item) => {
                    const fallbackImage = 'https://via.placeholder.com/100x100?text=No+Image';
                    const firstImage = Array.isArray(item.images) && item.images.length > 0 ? item.images[0] : undefined;
                    const imageSrc = item.image_url || firstImage || fallbackImage;
                    
                    return (
                      <div key={item.id} className="flex items-center space-x-4 p-4 bg-gray-50 hover:bg-gray-100 transition-colors duration-200">
                        <img 
                          src={imageSrc} 
                          alt={item.name}
                          onError={(e) => {
                            e.target.src = fallbackImage;
                          }}
                          className="w-16 h-16 object-cover rounded"
                        />
                        <div className="flex-1">
                          <h3 className="font-medium text-gray-900">{item.name}</h3>
                          <p className="text-gray-600 text-sm">EGP{item.price}</p>
                        </div>
                        <div className="flex items-center space-x-2">
                          <button
                            onClick={() => updateQuantity(item.id, item.quantity - 1)}
                            className="w-8 h-8 bg-gray-200 hover:bg-gray-300 transition-colors duration-200 flex items-center justify-center rounded"
                          >
                            -
                          </button>
                          <span className="w-12 text-center font-medium">{item.quantity}</span>
                          <button
                            onClick={() => updateQuantity(item.id, item.quantity + 1)}
                            className="w-8 h-8 bg-gray-200 hover:bg-gray-300 transition-colors duration-200 flex items-center justify-center rounded"
                          >
                            +
                          </button>
                        </div>
                        <button
                          onClick={() => removeFromCart(item.id)}
                          className="text-red-500 hover:text-red-700 transition-colors duration-200"
                        >
                          <Trash2 size={16} />
                        </button>
                      </div>
                    );
                  })}
                </div>
              )}
            </div>
            
            {cart.length > 0 && (
              <div className="p-6 border-t border-gray-200 bg-gray-50">
                <div className="flex justify-between items-center mb-4">
                  <span className="text-xl font-bold text-gray-900">Total:EGP {getTotalPrice().toFixed(2)}</span>
                </div>
                <button
                  onClick={handleCheckout}
                  className="w-full bg-black text-white py-3 font-medium tracking-wide uppercase transition-all duration-300 hover:bg-gray-800 transform hover:scale-[1.02] rounded"
                >
                  Proceed to Checkout
                </button>
              </div>
            )}
          </>
        ) : (
          <div className="p-6 overflow-y-auto max-h-96">
            <div className="bg-gradient-to-r from-green-50 to-blue-50 p-6 rounded-lg mb-6">
              <h3 className="text-xl font-bold text-gray-900 mb-4 flex items-center">
                <span className="text-2xl mr-2">✅</span>
                Order Confirmation Instructions
              </h3>
              
              <div className="space-y-4">
                <div className="bg-white p-4 rounded-lg shadow-sm">
                  <h4 className="font-semibold text-gray-900 mb-2">Order Summary</h4>
                  <div className="flex justify-between text-sm text-gray-600 mb-1">
                    <span>Total Amount:</span>
                    <span className="font-medium">EGP{getTotalPrice().toFixed(2)}</span>
                  </div>
                  <div className="flex justify-between text-lg font-bold text-green-600">
                    <span>Deposit Required (50%):</span>
                    <span>EGP{depositAmount}</span>
                  </div>
                </div>

                <div className="bg-white p-4 rounded-lg shadow-sm">
                  <h4 className="font-semibold text-gray-900 mb-3 flex items-center">
                    <Phone size={18} className="mr-2 text-blue-600" />
                    Step 1: Payment
                  </h4>
                  <p className="text-gray-700 text-sm leading-relaxed">
                    Please transfer the deposit <span className="font-bold text-green-600">EGP{depositAmount}</span> via Vodafone Cash to:
                  </p>
                  <div className="bg-gray-50 p-3 rounded mt-2 text-center">
                    <span className="font-bold text-lg text-gray-900">01016887251</span>
                  </div>
                </div>

                <div className="bg-white p-4 rounded-lg shadow-sm">
                  <h4 className="font-semibold text-gray-900 mb-3 flex items-center">
                    <MessageCircle size={18} className="mr-2 text-green-600" />
                    Step 2: Confirmation
                  </h4>
                  <p className="text-gray-700 text-sm leading-relaxed mb-3">
                    Send a screenshot of the payment confirmation via:
                  </p>
                  <div className="flex space-x-4">
                    <div className="flex items-center text-sm text-gray-600 bg-gray-50 px-3 py-2 rounded">
                      <Instagram size={16} className="mr-2 text-pink-500" />
                      Instagram DM
                    </div>
                    <div className="flex items-center text-sm text-gray-600 bg-gray-50 px-3 py-2 rounded">
                      <Phone size={16} className="mr-2 text-green-500" />
                      WhatsApp
                    </div>
                  </div>
                  <div className="bg-gray-50 p-3 rounded mt-2 text-center">
                    <span className="font-bold text-lg text-gray-900">01016887251</span>
                  </div>
                </div>

                <div className="bg-blue-50 p-4 rounded-lg border border-blue-200">
                  <p className="text-blue-800 text-sm leading-relaxed">
                    <span className="font-semibold">📞 Final Step:</span> Once completed, our team will contact you to finalize your order, secure your reservation, and arrange the delivery details.
                  </p>
                </div>
              </div>
            </div>

            <div className="flex space-x-3 mt-6">
              <button
                onClick={() => setShowInstructions(false)}
                className="flex-1 bg-gray-200 text-gray-800 py-3 px-4 font-medium tracking-wide uppercase transition-all duration-300 hover:bg-gray-300 rounded"
              >
                Back to Cart
              </button>
              <button
                onClick={handleGotIt}
                className="flex-1 bg-green-600 text-white py-3 px-4 font-medium tracking-wide uppercase transition-all duration-300 hover:bg-green-700 rounded"
              >
                Got it!
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default CartModal;
