import React, { useState, useEffect } from 'react';
import { ShoppingBag, Search, User, ShoppingCart, Menu, X } from 'lucide-react';
import { motion, AnimatePresence } from 'framer-motion';

const navItems = [
  { name: "Home", href: "#home", scrollTo: "home" },
  { name: "Products", href: "#products", scrollTo: "products" },
  { name: "About", href: "#about", scrollTo: "about" },
  { name: "Contact", href: "#contact", scrollTo: "contact" },
];

const listVariants = {
  hidden: {},
  visible: {
    transition: {
      staggerChildren: 0.15,
    },
  },
};

const itemVariants = {
  hidden: { opacity: 0, y: 20 },
  visible: { 
    opacity: 1, 
    y: 0,
    transition: { duration: 0.4, ease: "easeOut" }
  },
};

const Header = ({ cartCount, toggleCart, toggleMenu }) => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [isScrolled, setIsScrolled] = useState(false);

  // تأثير عند التمرير
  useEffect(() => {
    const handleScroll = () => setIsScrolled(window.scrollY > 10);
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // دالة للانتقال السلس للأقسام
  const scrollToSection = (sectionId) => {
    if (sectionId === 'home') {
      // للانتقال لأعلى الصفحة
      window.scrollTo({
        top: 0,
        behavior: 'smooth'
      });
    } else {
      // للانتقال للأقسام الأخرى
      const element = document.getElementById(sectionId);
      if (element) {
        // حساب المسافة مع أخذ ارتفاع الهيدر في الاعتبار
        const headerHeight = 80;
        const elementPosition = element.offsetTop - headerHeight;
        
        window.scrollTo({
          top: elementPosition,
          behavior: 'smooth'
        });
      }
    }
  };

  return (
    <motion.header
      initial={{ y: -80, opacity: 0 }}
      animate={{ y: 0, opacity: 1 }}
      transition={{ duration: 0.6, ease: "easeOut" }}
      className={`bg-black/95 backdrop-blur-sm border-b border-gray-800 sticky top-0 z-50 transition-all duration-300 ${
        isScrolled ? 'shadow-2xl py-2' : 'shadow-lg py-4'
      }`}
    >
      <div className="container mx-auto px-4">
        <div className="flex justify-between items-center">
          {/* الشعار */}
          <motion.div 
            className="flex items-center space-x-3 space-x-reverse cursor-pointer"
            whileHover={{ scale: 1.05 }}
            transition={{ type: "spring", stiffness: 300 }}
            onClick={() => scrollToSection('home')}
          >
            <div className="relative">
              <ShoppingBag className="w-8 h-8 text-white" />
              <div className="absolute -bottom-1 -right-1 w-2 h-2 bg-gray-400 rounded-full"></div>
            </div>
            <h1 className="text-2xl font-bold text-white">
              SK 
            </h1>
          </motion.div>

          {/* قائمة التنقل */}
          <motion.nav 
            className="hidden md:flex items-center space-x-10 space-x-reverse"
            variants={listVariants}
            initial="hidden"
            animate="visible"
          >
            {navItems.map((item, index) => (
              <motion.button
                key={index}
                onClick={() => scrollToSection(item.scrollTo)}
                className="text-gray-300 hover:text-white transition-colors font-medium relative group cursor-pointer bg-transparent border-none"
                variants={itemVariants}
                whileHover={{ y: -3, scale: 1.05 }}
              >
                {item.name}
                <span className="absolute -bottom-1 left-0 w-0 h-0.5 bg-white transition-all group-hover:w-full"></span>
              </motion.button>
            ))}
          </motion.nav>

          {/* الأزرار */}
          <div className="flex items-center space-x-3 space-x-reverse">
            {/* البحث */}
            <div className={`relative flex items-center ${isSearchOpen ? 'w-48' : 'w-10'} transition-all`}>
              <motion.button
                whileTap={{ scale: 0.9 }}
                onClick={() => setIsSearchOpen(!isSearchOpen)}
                className="p-2 hover:bg-gray-800 rounded-full transition-all absolute right-0 z-10"
              >
                {isSearchOpen ? (
                  <X className="w-5 h-5 text-gray-300" />
                ) : (
                  <Search className="w-5 h-5 text-gray-300" />
                )}
              </motion.button>

              <AnimatePresence>
                {isSearchOpen && (
                  <motion.input
                    key="search"
                    initial={{ width: 0, opacity: 0 }}
                    animate={{ width: "100%", opacity: 1 }}
                    exit={{ width: 0, opacity: 0 }}
                    transition={{ duration: 0.3 }}
                    type="text"
                    placeholder="...Search"
                    className="h-10 pr-10 pl-3 rounded-full bg-gray-800 border border-gray-600 text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-white focus:border-transparent text-sm"
                    autoFocus
                  />
                )}
              </AnimatePresence>
            </div>

            {/* المستخدم */}
            <motion.button
              whileHover={{ scale: 1.1 }}
              className="p-2 hover:bg-gray-800 rounded-full transition-colors hidden sm:block"
            >
              <User className="w-5 h-5 text-gray-300" />
            </motion.button>

            {/* السلة */}
            <motion.button
              whileHover={{ scale: 1.1 }}
              onClick={toggleCart}
              className="relative p-2 hover:bg-gray-800 rounded-full transition-colors group"
            >
              <ShoppingCart className="w-5 h-5 text-gray-300 group-hover:text-white transition-colors" />
              {cartCount > 0 && (
                <motion.span
                  initial={{ scale: 0 }}
                  animate={{ scale: 1 }}
                  transition={{ type: "spring", stiffness: 200 }}
                  className="absolute -top-1 -right-1 bg-white text-black text-xs rounded-full w-5 h-5 flex items-center justify-center font-semibold"
                >
                  {cartCount}
                </motion.span>
              )}
            </motion.button>

            {/* المنيو للموبايل */}
            <motion.button
              whileHover={{ rotate: 90 }}
              onClick={toggleMenu}
              className="md:hidden p-2 hover:bg-gray-800 rounded-full transition-colors ml-2"
            >
              <Menu className="w-5 h-5 text-gray-300" />
            </motion.button>
          </div>
        </div>
      </div>
    </motion.header>
  );
};

export default Header;
